var book = {
	"name": "Prvá Kroník",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adam, Šét, Enóš,",
			"2": "<sup>2</sup> Kenán, Mahalalel, Jered,",
			"3": "<sup>3</sup> Henoch, Matuzalem, Lemech,",
			"4": "<sup>4</sup> Noach, Šém, Chám a Jefet.",
			"5": "<sup>5</sup> Jefetovi synovia: Gomer, Magóg, Madaj, Javán, Tubal, Mešech a Tirás.",
			"6": "<sup>6</sup> Gomerovi synovia: Aškenaz, Difat a Togarma.",
			"7": "<sup>7</sup> Javánovi synovia: Eliša, Taršíš, Kittejci a Rodaním.",
			"8": "<sup>8</sup> Chámovi synovia: Kúš, Micrajim, Pút a Kanaán.",
			"9": "<sup>9</sup> Kúšovi synovia: Sába, Chavila, Sabta, Raema, Sabtecha. Raemovi synovia: Šeba a Dedán.",
			"10": "<sup>10</sup> Kúšovi sa narodil Nimród. Stal sa prvým hrdinom na svete.",
			"11": "<sup>11</sup> Micrajim splodil: Lúdov, Anámov, Lehábov, Naftuchov,",
			"12": "<sup>12</sup> Patrusanov a Kasluchov, z ktorých pochádzajú Filištínci a Kaftóri.",
			"13": "<sup>13</sup> Kanaán splodil Sidona, svojho prvorodeného, a Chéta,",
			"14": "<sup>14</sup> ďalej Jebúsejov, Amorejčanov, Girgašejov,",
			"15": "<sup>15</sup> Chivvijov, Arkijov, Sínejov,",
			"16": "<sup>16</sup> Arvádov, Cemarov a Chamátov.",
			"17": "<sup>17</sup> Šémovi synovia: Elám, Aššúr, Arpachšad, Lúd, Aram, Úc, Chúl, Geter a Mešech.",
			"18": "<sup>18</sup> Arpachšad splodil Šelacha a Šelach splodil Ebera.",
			"19": "<sup>19</sup> Eberovi sa narodili dvaja synovia. Jeden sa volal Peleg, lebo za jeho čias bola rozdelená zem, a jeho brat sa volal Joktán.",
			"20": "<sup>20</sup> Joktán splodil: Almodáda, Šelefa, Chacarmáveta, Jaracha,",
			"21": "<sup>21</sup> Hadoráma, Uzála, Diklu,",
			"22": "<sup>22</sup> Ebála, Abimaela, Šebu,",
			"23": "<sup>23</sup> Ofíra, Chavilu a Jobába. Títo všetci boli synovia Joktána.",
			"24": "<sup>24</sup> Šém, Arpachšad, Šelach,",
			"25": "<sup>25</sup> Eber, Peleg, Reu,",
			"26": "<sup>26</sup> Serúg, Nachór, Terach,",
			"27": "<sup>27</sup> Abrám, inak Abrahám.",
			"28": "<sup>28</sup> Synovia Abraháma: Izák a Izmael.",
			"29": "<sup>29</sup> Ich rodokmeň je tento: Izmaelov prvorodenec Nebajót, ďalej Kedár, Adbeel, Mibsám,",
			"30": "<sup>30</sup> Mišma, Duma, Massa, Chadad, Tema,",
			"31": "<sup>31</sup> Jetúr, Nafíš a Kédema. To boli Izmaelovi synovia.",
			"32": "<sup>32</sup> Synovia, ktorých porodila Ketura, vedľajšia žena Abrahámova: Zimrán, Jokšán, Medán, Midján, Jišbák a Šúach. Jokšánovi synovia: Šeba a Dedán.",
			"33": "<sup>33</sup> Midjánovi synovia: Efa, Éfer, Chanóch, Abida a Eldaa. Tí všetci boli synovia Ketury.",
			"34": "<sup>34</sup> Abrahám splodil ešte Izáka. Izákovi synovia: Ezáv a Izrael.",
			"35": "<sup>35</sup> Ezávovi synovia: Elifaz, Reuel, Jeúš, Jalám a Korach.",
			"36": "<sup>36</sup> Elifazovi synovia: Temán, Omár, Cefi, Gatám, Kenaz, Timna a Amalék.",
			"37": "<sup>37</sup> Reuelovi synovia: Nachat, Zerach, Šamma a Mizza.",
			"38": "<sup>38</sup> Seírovi synovia: Lotán, Šobál, Cibeón, Ana, Dišón, Ecer a Dišán.",
			"39": "<sup>39</sup> Lotánovi synovia: Chori a Homám. Lotánova sestra bola Timna.",
			"40": "<sup>40</sup> Šobálovi synovia: Alján, Manachat, Ebál, Šefi a Onám. Cibeónovi synovia: Aja a Ana.",
			"41": "<sup>41</sup> Anov syn bol Dišón. Dišónovi synovia: Chamrán, Ešbán, Jitrán a Kerán.",
			"42": "<sup>42</sup> Ecerovi synovia: Bilhán, Zaaván a Jaakán. Dišánovi synovia: Úc a Arán.",
			"43": "<sup>43</sup> Toto sú králi, ktorí panovali v Edómsku, prv než mali kráľa Izraeliti: Bela, syn Beóra. Jeho sídlo sa volalo Dinhaba.",
			"44": "<sup>44</sup> Po Belovej smrti kraľoval Zerachov syn Jobáb z Bocry.",
			"45": "<sup>45</sup> Po Jobábovej smrti kraľoval Chušám z krajiny Temáncov.",
			"46": "<sup>46</sup> Po Chušámovej smrti kraľoval Bedadov syn Hadad, ktorý porazil Midjánčanov na moábskom poli. Jeho sídlo sa volalo Avít.",
			"47": "<sup>47</sup> Po Hadadovej smrti kraľoval Samla z Masreky.",
			"48": "<sup>48</sup> Po Samlovej smrti kraľoval Šaúl z Rechobótu pri Veľrieke.",
			"49": "<sup>49</sup> Po Šaúlovej smrti kraľoval Achbórov syn Baal-Chanán.",
			"50": "<sup>50</sup> Po smrti Baal-Chanána kraľoval Hadad. Jeho sídlo sa volalo Pau a jeho žena sa volala Mehetabel, dcéra Matréda, vnučka Me-Zahába.",
			"51": "<sup>51</sup> Po Hadadovej smrti vládli v Edóme náčelníci: náčelník Timna, náčelník Alva, náčelník Jetét,",
			"52": "<sup>52</sup> náčelník Oholibama, náčelník Ela, náčelník Pinón,",
			"53": "<sup>53</sup> náčelník Kenaz, náčelník Temán, náčelník Mibcár,",
			"54": "<sup>54</sup> náčelník Magdíel a náčelník Irám. To boli edómski náčelníci."
		},
		"2": {
			"1": "<sup>1</sup> Toto sú Izraelovi synovia: Rúben, Šimeón, Lévi, Júda, Jissachár, Zebulún,",
			"2": "<sup>2</sup> Dán, Jozef, Benjamín, Naftali, Gád a Ašér.",
			"3": "<sup>3</sup> Júdovi synovia: Ér, Onán a Šela, traja, ktorých mal so Šúovou dcérou, Kanaánčankou. Ér, Júdov prvorodený, sa však znepáčil Hospodinovi, preto ho usmrtil.",
			"4": "<sup>4</sup> Jeho nevesta Tamar mu porodila Pereca a Zeracha. Všetkých Júdových synov bolo päť.",
			"5": "<sup>5</sup> Perecovi synovia: Checrón a Chamúl.",
			"6": "<sup>6</sup> Zerachovi synovia: Zimri, Etán, Hemán, Kalkól a Dara, spolu päť.",
			"7": "<sup>7</sup> Karmiho syn bol Achár, ktorý priviedol Izrael do skazy tým, že nedodržal hubiacu kliatbu.",
			"8": "<sup>8</sup> Etánovým synom bol Azarja.",
			"9": "<sup>9</sup> Checrónovi sa narodili synovia: Jerachmeel, Rám a Kelubaj.",
			"10": "<sup>10</sup> Rám splodil Amminadába, Amminadáb splodil Nachšóna, knieža Júdovcov.",
			"11": "<sup>11</sup> Nachšón splodil Salmu a Salma splodil Boaza.",
			"12": "<sup>12</sup> Boaz splodil Obéda, Obéd splodil Izaja.",
			"13": "<sup>13</sup> Izaj splodil Eliába ako prvorodeného, Abinadába druhého, Šimeu tretieho,",
			"14": "<sup>14</sup> Netaneela štvrtého, Radaja piateho,",
			"15": "<sup>15</sup> Ocema šiesteho, Dávida siedmeho.",
			"16": "<sup>16</sup> Ich sestry boli Ceruja a Abigajil. Synovia Ceruje: Abšaj, Joáb a Asael, traja.",
			"17": "<sup>17</sup> Abigajil porodila Amasu a Amasovým otcom bol Izmaelita Jeter.",
			"18": "<sup>18</sup> Kaléb, Checrónov syn, splodil so ženami Azubou a Jeriót týchto synov: Ješera, Šobába a Ardóna.",
			"19": "<sup>19</sup> Po smrti Azuby si vzal Kaléb Efratu a tá mu porodila Chúra.",
			"20": "<sup>20</sup> Chúr splodil Uriho a Uri splodil Becaleela.",
			"21": "<sup>21</sup> Potom vošiel Checrón k dcére Machíra, Gileádovho otca. Mal šesťdesiat rokov, keď si ju vzal, porodila mu Segúba.",
			"22": "<sup>22</sup> Segúb splodil Jaíra, ktorý mal dvadsaťtri miest v Gileádskom kraji.",
			"23": "<sup>23</sup> No Gešúrci a Aramejčania im odobrali Chavvót-Jaír, Kenát a jeho osady, spolu šesťdesiat miest. Tí všetci sú synovia Machíra, Gileádovho otca.",
			"24": "<sup>24</sup> Po Checrónovej smrti vošiel Kaléb k Efrate, žene svojho otca Checróna, a porodila mu Ašchúra, otca Tekóu.",
			"25": "<sup>25</sup> Synovia Jerachmeela, Checrónovho prvorodeného, boli: prvorodený syn Rám, ďalej Buna, Oren, Ocem a Achija.",
			"26": "<sup>26</sup> Jerachmeel mal aj druhú ženu menom Atara; tá bola Onámovou matkou.",
			"27": "<sup>27</sup> Synovia Ráma, Jerachmeelovho prvorodeného, boli: Maac, Jamín a Eker.",
			"28": "<sup>28</sup> Onámovi synovia boli: Šammaj a Jada. Šammajovi synovia: Nadáb a Abišúr.",
			"29": "<sup>29</sup> Abišúrova žena sa volala Abihajil. Tá mu porodila Achbána a Molída.",
			"30": "<sup>30</sup> Nadábovi synovia: Seled a Appajim. Seled nezanechal synov.",
			"31": "<sup>31</sup> Appajimov syn bol Jišei, syn Jišeiho Šešán a syn Šešána Achlaj.",
			"32": "<sup>32</sup> Synovia Jadu, Šammajovho brata: Jeter a Jonatán. Jeter nezanechal synov.",
			"33": "<sup>33</sup> Jonatánovi synovia: Pelet a Zaza. To boli synovia Jerachmeela.",
			"34": "<sup>34</sup> Šešán nemal synov, iba dcéry. Mal však egyptského sluhu menom Jarcha.",
			"35": "<sup>35</sup> Tomu dal svoju dcéru za ženu. Porodila mu Attaja.",
			"36": "<sup>36</sup> Attaj splodil Nátana, Nátan splodil Zabáda,",
			"37": "<sup>37</sup> Zabád splodil Eflála, Eflál splodil Obéda,",
			"38": "<sup>38</sup> Obéd splodil Jehua a Jehu splodil Azarju.",
			"39": "<sup>39</sup> Azarja splodil Cheleca, Chelec splodil Elasu,",
			"40": "<sup>40</sup> Elasa splodil Sismaja a Sismaj splodil Šallúma,",
			"41": "<sup>41</sup> Šallúm splodil Jekamju a Jekamja splodil Elišamu.",
			"42": "<sup>42</sup> Synovia Kaléba, Jerachmeelovho brata: prvorodený Meša, Zífov otec, a druhý syn Mareša, Chebrónov otec.",
			"43": "<sup>43</sup> Chebrónovi synovia: Korach, Tappúach, Rekem a Šema.",
			"44": "<sup>44</sup> Šema splodil Rachama, otca Jorkoáma a Rekem splodil Šammaja.",
			"45": "<sup>45</sup> Šammajovým synom bol Maón a Maón bol otcom Bét-Cúra.",
			"46": "<sup>46</sup> Efa, vedľajšia Kalébova žena, porodila Charána, Mocu a Gazéza. Charán splodil Gazéza.",
			"47": "<sup>47</sup> Jahdajovi synovia: Regem, Jotám, Gešán, Pelet, Efa a Šaaf.",
			"48": "<sup>48</sup> Maacha, Kalébova vedľajšia žena, porodila Šebera a Tirchanu.",
			"49": "<sup>49</sup> Ďalej porodila Šaafa, otca Madmannu, Ševu, otca Machbenu a otca Gibeu. Kalébova dcéra bola Achsa.",
			"50": "<sup>50</sup> Títo boli synovia Kaléba: Chúr, prvorodený Efraty, Šobál, otec Kirjat-Jearíma,",
			"51": "<sup>51</sup> Salma, otec Betlehema, Charéf, otec Bét-Gadéra.",
			"52": "<sup>52</sup> Šobál, otec Kirjat-Jearíma, mal synov: Haroeho a polovicu Menuchóťanov.",
			"53": "<sup>53</sup> Rody Kirjat-Jearíma tvorili Jitrijci, Pútovci, Šumátovci a Mišraovci. Z nich pochádzali Coreanci a Eštaulci.",
			"54": "<sup>54</sup> Salmovi synovia: Betlehem, Netofania, Atarót-Bét-Joáb, druhá polovica Manachatejov, inak Coreancov.",
			"55": "<sup>55</sup> Rody pisárov, ktorí bývali v Jabéci: Tiratovci, Šimeatovci a Suchatovci. Oni boli Kéniti, ktorí pochádzali od Chammata, otca domu Rechába."
		},
		"3": {
			"1": "<sup>1</sup> Toto boli synovia Dávida, ktorí sa mu narodili v Hebrone: prvorodený Amnón od Jezreelčanky Achinoam, druhý Daniel od Karmelčanky Abigajil,",
			"2": "<sup>2</sup> tretí Absolón od Maachy, dcéry gešúrskeho kráľa Talmaja, štvrtý Adonija, syn Chaggít,",
			"3": "<sup>3</sup> piaty Šefatja od Abitál, šiesty Jitreám od jeho ženy Egly.",
			"4": "<sup>4</sup> Šiesti sa mu narodili v Hebrone, kde vládol sedem rokov a šesť mesiacov; v Jeruzaleme vládol tridsaťtri rokov.",
			"5": "<sup>5</sup> Títo sa mu narodili v Jeruzaleme: Šimea, Šobáb, Nátan, Šalamún, štyria od Batšeby, Ammíelovej dcéry,",
			"6": "<sup>6</sup> ďalej Jibchár, Elišama, Elifelet,",
			"7": "<sup>7</sup> Nogah, Nefeg, Jafia,",
			"8": "<sup>8</sup> Elišama, Eljada a Elifelet, deviati.",
			"9": "<sup>9</sup> To všetko boli Dávidovi synovia okrem synov od vedľajších žien. Tamar bola ich sestrou.",
			"10": "<sup>10</sup> Šalamúnov syn bol Rechabeám a jeho syn Abija, jeho syn Asa, jeho syn Jošafát,",
			"11": "<sup>11</sup> jeho syn Jorám, jeho syn Achazja, jeho syn Joáš,",
			"12": "<sup>12</sup> jeho syn Amacja, jeho syn Azarja, jeho syn Jotám,",
			"13": "<sup>13</sup> jeho syn Acház, jeho syn Chizkija, jeho syn Menašše,",
			"14": "<sup>14</sup> jeho syn Amón, jeho syn Joziáš.",
			"15": "<sup>15</sup> Joziášovi synovia: prvorodený Jochanán, druhý Jojakím, tretí Cidkija, štvrtý Šallúm.",
			"16": "<sup>16</sup> Jojakímovi synovia: jeho syn Jechonja, jeho syn Cidkija.",
			"17": "<sup>17</sup> Synovia väzňa Jechonju: jeho syn Šealtíel,",
			"18": "<sup>18</sup> ďalej Malkirám, Pedaja, Šenaccar, Jekamja, Hošama a Nedabja.",
			"19": "<sup>19</sup> Pedajovi synovia: Zerubbábel a Šimei. Zerubbábelovi synovia: Mešullám, Chananja. Ich sestrou bola Šelomít.",
			"20": "<sup>20</sup> Ďalej Chašuba, Ohel, Berechja, Chasadja a Jušab-Chesed, piati.",
			"21": "<sup>21</sup> Chananjovi synovia: Pelatja a Ješaja, Refajovi synovia, Arnánovi synovia, Obadjovi synovia, Šechanjovi synovia.",
			"22": "<sup>22</sup> Šechanjov syn: Šemaja. Šemajovi synovia: Chattúš, Jigál, Baríach, Nearja a Šafát, šiesti.",
			"23": "<sup>23</sup> Nearjovi synovia: Eljoenaj, Chizkija a Azrikám, traja.",
			"24": "<sup>24</sup> Eljoenajovi synovia: Hodavja, Eljašíb, Pelaja, Akkúb, Jochanán, Delaja a Anani, siedmi."
		},
		"4": {
			"1": "<sup>1</sup> Júdovi synovia: Perec, Checrón, Karmi, Chúr a Šobál.",
			"2": "<sup>2</sup> Reaja, Šobálov syn, splodil Jachata, Jachat splodil Achumaja a Lahada. To sú coreanské rody.",
			"3": "<sup>3</sup> Toto sú Etámovi synovia: Jezreel, Jišma a Jidbáš, ich sestra sa volala Haclelponi,",
			"4": "<sup>4</sup> ďalej Penuel, otec Gedóra, a Ezer, otec Chušu. To sú synovia Chúra, prvorodeného Efraty, otca Betlehema.",
			"5": "<sup>5</sup> Ašchúr, otec Tekóu, mal dve ženy: Chelu a Naaru.",
			"6": "<sup>6</sup> Naara mu porodila Achuzzáma, Chefera, Temnovcov a Achaštarejcov. To sú synovia Naary.",
			"7": "<sup>7</sup> Synovia Chely: Ceret, Jecóchar, Etnán.",
			"8": "<sup>8</sup> Kóc splodil Anúba, Haccobebu a rody Acharchela, Harúmovho syna.",
			"9": "<sup>9</sup> Jabbéc bol vo väčšej úcte než jeho bratia. Jeho matka mu dala meno Jabbéc so slovami: „V bolesti som ho porodila.“",
			"10": "<sup>10</sup> Jabbéc vyslovil Bohu Izraela želanie: „Kiežby si ma požehnával a rozšíril moje územie. Tvoja ruka nech ma sprevádza a zbaví zlého, aby som nemal trápenie!“ Boh jeho prosbu splnil.",
			"11": "<sup>11</sup> Kelúb, Šuchov brat, splodil Mechíra, Eštónovho otca.",
			"12": "<sup>12</sup> Eštón splodil Bét-Rafu, Paséacha, Techinnu, otca Ír-Nacháša. To sú muži z Rechy.",
			"13": "<sup>13</sup> Kenazovi synovia: Otníel a Seraja. Synovia Otníela: Chatat a Meonotaj.",
			"14": "<sup>14</sup> Meonotaj splodil Ofru, Seraja splodil Joába, otca Gé-Charašíma, boli tam totiž remeselníci.",
			"15": "<sup>15</sup> Synovia Kaléba, Jefunneho syna: Iru, Ela a Naam. Elov syn bol Kenaz.",
			"16": "<sup>16</sup> Jehallelelovi synovia: Zíf, Zifa, Tirja a Asarel.",
			"17": "<sup>17</sup> Ezrovi synovia: Jeter, Mered, Éfer a Jalón. Jeter splodil Mirjáma, Šammaja a Jišbácha, otca Eštemou.",
			"18": "<sup>18</sup> Jeho judská žena porodila Jereda, otca Gedóra, Chebera, otca Socha, Jekutíela, otca Zanóacha. To sú synovia Bitje, faraónovej dcéry, ktorú si vzal Mered.",
			"19": "<sup>19</sup> Synovia Hodijovej ženy, sestry Nachama, otca Keilu Garmijského a Eštemou Maachátskeho.",
			"20": "<sup>20</sup> Šimónovi synovia: Amnón, Rinna, Ben-Chanán a Tilón. Jišeiho synovia: Zochét a Ben-Zochét.",
			"21": "<sup>21</sup> Synovia Šelu, Júdovho syna: Ér, otec Lechu, Lada, otec Marešu, a rody tkáčov ľanu z Bét-Ašbey,",
			"22": "<sup>22</sup> ďalej Jokím, muži z Kozeby Joáš a Saráf, ktorí vládli v Moábsku a vrátili sa do Lechemu. To sú však dávne príbehy.",
			"23": "<sup>23</sup> Boli to hrnčiari a obyvatelia Netaímu a Gedery. Bývali tam pri kráľovi v jeho službe.",
			"24": "<sup>24</sup> Šimeónovi synovia: Nemuel, Jamín, Jaríb, Zerach, Šaúl.",
			"25": "<sup>25</sup> Jeho syn Šallúm, jeho syn Mibsám, jeho syn Mišma.",
			"26": "<sup>26</sup> Synovia Mišmu: jeho syn Chammuel, jeho syn Zakkúr, jeho syn Šimei.",
			"27": "<sup>27</sup> Šimei mal šestnásť synov a šesť dcér, no jeho bratia neoplývali synmi, takže všetky ich rody neboli také početné ako Júdovci.",
			"28": "<sup>28</sup> Bývali v Beér-Šebe, v Molade v Chacar-Šuále,",
			"29": "<sup>29</sup> v Bilhe, v Eceme, v Tolade,",
			"30": "<sup>30</sup> v Betueli, v Chorme, v Ciklagu,",
			"31": "<sup>31</sup> v Bét-Markabóte, v Chacar-Susíme, v Bét-Biri a v Šaarajime. To boli ich mestá až do Dávidovho kraľovania.",
			"32": "<sup>32</sup> Ich dediny boli: Etám, Ajin, Rimmón, Tóchen, Ašán, päť miest",
			"33": "<sup>33</sup> a všetky dediny v okolí týchto miest až po Baal. To boli ich sídla. Mali i svoj rodový zoznam.",
			"34": "<sup>34</sup> Mešobáb, Jamléch, Amacjov syn Joša,",
			"35": "<sup>35</sup> Joel, Jehu, syn Jošibju, syna Seraju, syna Asíela,",
			"36": "<sup>36</sup> Eljoenaj, Jaakoba, Ješochaja, Asaja, Adíel, Jesimíel, Benaja,",
			"37": "<sup>37</sup> Ziza, syn Šifeiho, syna Allóna, syna Jedaju, syna Šimriho, syna Šemaju.",
			"38": "<sup>38</sup> Títo menovaní boli kniežatami vo svojich rodoch. Ich rodiny sa veľmi rozmnožili.",
			"39": "<sup>39</sup> Odišli smerom ku Gedóru a na východ údolia hľadať si pašu pre svoje stáda.",
			"40": "<sup>40</sup> Našli výdatnú a dobrú pašu. Krajina na obe strany priestranná bola pokojná a bezpečná, lebo krajinu predtým obývali Chámovci.",
			"41": "<sup>41</sup> Títo označení menami prišli za čias judského kráľa Chizkiju, rozbili ich stany a na Meuncoch, ktorých tam našli, vykonali hubiacu kliatbu a tak je to dodnes. Usadili sa na ich mieste, lebo tam mali pastvu pre svoje stáda.",
			"42": "<sup>42</sup> Časť Šimeónovcov, asi päťsto mužov, odišla k seírskemu pohoriu. V ich čele stáli Pelatja, Nearja, Refaja, Uzzíel, Jišeiho synovia.",
			"43": "<sup>43</sup> Pobili zvyšok obyvateľov po Amalékovi a bývajú tam dodnes."
		},
		"5": {
			"1": "<sup>1</sup> Synovia Rúbena, Izraelovho prvorodeného. Bol síce prvorodený, no keď zneuctil lôžko svojho otca, prvorodenstvo prešlo na synov Jozefa, Izraelovho syna, takže v rodovom zozname nevystupoval ako prvorodený.",
			"2": "<sup>2</sup> Hoci mal Júda početnú prevahu nad svojimi bratmi, ba i vojvodca vyšiel z neho, prvorodenstvo pripadlo Jozefovi.",
			"3": "<sup>3</sup> Synovia Rúbena, Izraelovho prvorodeného: Chanóch, Pallu, Checrón a Karmi.",
			"4": "<sup>4</sup> Synovia Joelovi: jeho syn Šemaja, jeho syn Góg, jeho syn Šimei,",
			"5": "<sup>5</sup> jeho syn Micha, jeho syn Reaja, jeho syn Baal,",
			"6": "<sup>6</sup> jeho syn Beera, ktorého odvliekol asýrsky kráľ Tiglat-Pileser do zajatia. Bol kniežaťom Rúbenovcov.",
			"7": "<sup>7</sup> Jeho bratia po rodoch, ako boli zapísaní do rodového zoznamu: predák Jeíel, ďalej Zecharja,",
			"8": "<sup>8</sup> Bela, syn Azáza, syna Šemu, Joelovho syna, ktorý obýval Aroér až po Nebó a Baal-Meón.",
			"9": "<sup>9</sup> Smerom na východ býval po okraj púšte, ktorá sa tiahne od rieky Eufrat. Mali totiž v Gileádskom kraji početné stáda.",
			"10": "<sup>10</sup> Za čias Šaula viedli vojnu proti Hagrijcom. Tí padli do ich moci a nato sa usadili v ich stanoch na celej východnej strane Gileádu.",
			"11": "<sup>11</sup> Gádovi synovia obývali oproti nim bášanský kraj až po Salchu.",
			"12": "<sup>12</sup> Predákom bol Joel, druhý Šafám, ďalší Janaj a Šafát v Bášane.",
			"13": "<sup>13</sup> Ich rodní bratia boli: Michael, Mešullám, Šeba, Joraj, Jakán, Zía a Eber, siedmi.",
			"14": "<sup>14</sup> To boli synovia Abichajila, syna Churiho, syna Jaróacha, syna Gileáda, syna Michaela, syna Ješišaja, syna Jachda, syna Búza.",
			"15": "<sup>15</sup> Achi, syn Abdíela, syna Guniho, bol predákom ich rodín.",
			"16": "<sup>16</sup> Bývali v Gileáde, v Bášane, v jeho osadách a na všetkých pastvinách Šarónu až po ich okraje.",
			"17": "<sup>17</sup> Tí všetci boli zapísaní do rodového zoznamu za čias judského kráľa Jotáma a izraelského kráľa Jarobeáma.",
			"18": "<sup>18</sup> Rúbenovci, Gádovci a východní Menaššeovci boli udatní muži. Nosili štít a meč, ovládali lukostreľbu a mali vojnové skúsenosti. Bolo ich štyridsaťštyritisícsedemstošesťdesiat bojovníkov.",
			"19": "<sup>19</sup> Viedli vojnu proti Hagrijcom, proti Jetúrovi, Nafíšovi a Nodábovi.",
			"20": "<sup>20</sup> Dostalo sa im proti nim pomoci, takže Hagrijci i všetci ich spojenci padli do ich moci, lebo keď v boji volali o pomoc k Bohu, dal sa uprosiť, pretože dúfali v neho.",
			"21": "<sup>21</sup> Odviedli im stáda: päťdesiattisíc tiav, dvestopäťdesiattisíc oviec, dvetisíc oslov a stotisíc osôb.",
			"22": "<sup>22</sup> Padlo i mnoho ranených, lebo to bol Boží boj. Na ich mieste bývali až do zajatia.",
			"23": "<sup>23</sup> Príslušníci východnej časti kmeňa Menašše obývali územie od Bášanu po Baal-Chermón, totiž po Senír a pohorie Chermónu. Bolo ich mnoho.",
			"24": "<sup>24</sup> Toto boli predáci ich rodín: Éfer, Jišei, Elíel, Azríel, Jirmeja, Hodavja a Jachdíel, udatní hrdinovia, slávni muži, predáci svojich rodín.",
			"25": "<sup>25</sup> Spreneverili sa však Bohu svojich otcov a dopúšťali sa smilstva s božstvami národov zeme, ktoré Boh pred nimi vyhladil.",
			"26": "<sup>26</sup> Boh Izraela prebudil ducha asýrskeho kráľa Púla, inak Tiglat-Pilesera, ktorý odvliekol do zajatia Rúbenovcov, Gádovcov i východnú časť kmeňa Menašše a priviedol ich do Chalachu, Chabóru, Hary a k rieke Gozán, kde sú dodnes.",
			"27": "<sup>27</sup> Léviho synovia boli Geršón, Kohát a Merari.",
			"28": "<sup>28</sup> Kohátovi synovia: Amrám, Jishár, Chebrón a Uzzíel.",
			"29": "<sup>29</sup> Amrámovi synovia: Áron, Mojžiš, k tomu Mirjam. Áronovi synovia: Nadáb, Abihu, Eleazár a Itamár.",
			"30": "<sup>30</sup> Eleazár splodil Pinchása, Pinchás splodil Abišúu,",
			"31": "<sup>31</sup> Abišúa splodil Bukkiho, Bukki splodil Uzziho,",
			"32": "<sup>32</sup> Uzzi splodil Zerachju, Zerachja splodil Merajóta,",
			"33": "<sup>33</sup> Merajót splodil Amarju, Amarja splodil Achitúba,",
			"34": "<sup>34</sup> Achitúb splodil Cadóka, Cadók splodil Achimaaca,",
			"35": "<sup>35</sup> Achimaac splodil Azarju, Azarja splodil Jochanána,",
			"36": "<sup>36</sup> Jochanán splodil Azarju — to je ten, ktorý zastával kňazskú službu v chráme, čo v Jeruzaleme postavil Šalamún.",
			"37": "<sup>37</sup> Azarja splodil Amarju, Amarja splodil Achitúba,",
			"38": "<sup>38</sup> Achitúb splodil Cadóka, Cadók splodil Šallúma,",
			"39": "<sup>39</sup> Šallúm splodil Chilkiju, Chilkija splodil Azarju,",
			"40": "<sup>40</sup> Azarja splodil Seraju, Seraja splodil Jocadáka,",
			"41": "<sup>41</sup> Jocadák však odišiel, keď dal Hospodin odvliecť obyvateľov Júdu a Jeruzalema do zajatia prostredníctvom Nebukadnesara."
		},
		"6": {
			"1": "<sup>1</sup> Léviho synovia: Geršóm, Kohát, Merari.",
			"2": "<sup>2</sup> Toto sú mená Geršómových synov: Libni a Šimei.",
			"3": "<sup>3</sup> Kohátovi synovia: Amrám, Jishár, Chebrón a Uzzíel.",
			"4": "<sup>4</sup> Merariho synovia: Machli a Muši. Toto sú rody Léviho podľa ich rodín:",
			"5": "<sup>5</sup> Geršómov syn Libni, jeho syn Jachat, jeho syn Zimma,",
			"6": "<sup>6</sup> jeho syn Joách, jeho syn Iddo, jeho syn Zerach, jeho syn Jeotraj.",
			"7": "<sup>7</sup> Kohátovi synovia: jeho syn Amminadáb, jeho syn Korach, jeho syn Assír,",
			"8": "<sup>8</sup> jeho syn Elkana, jeho syn Ebjasáf, jeho syn Assír,",
			"9": "<sup>9</sup> jeho syn Tachat, jeho syn Uríel, jeho syn Uzzija, jeho syn Šaúl.",
			"10": "<sup>10</sup> Elkanovi synovia: Amasaj a Achimót,",
			"11": "<sup>11</sup> jeho syn Elkana, jeho syn Cofaj, jeho syn Nachat,",
			"12": "<sup>12</sup> jeho syn Eliáb, jeho syn Jerochám, jeho syn Elkana, jeho syn Samuel.",
			"13": "<sup>13</sup> Samuelovi synovia: prvorodený Joel a druhý Abija.",
			"14": "<sup>14</sup> Merariho synovia: Machli, jeho syn Libni, jeho syn Šimei, jeho syn Uzza,",
			"15": "<sup>15</sup> jeho syn Šimea, jeho syn Chaggija, jeho syn Asaja.",
			"16": "<sup>16</sup> Týmto zveril Dávid vedenie spevu v Hospodinovom dome, odkedy tam spočinula archa.",
			"17": "<sup>17</sup> Pri speve sa konala služba pred príbytkom stanu stretávania, kým nepostavil Šalamún v Jeruzaleme Hospodinov dom. Do služby nastupovali podľa svojho poriadku.",
			"18": "<sup>18</sup> Do služby nastupovali so svojimi synmi títo: zo synov Kohátových: spevák Hemán, syn Joela, syna Samuela,",
			"19": "<sup>19</sup> syna Elkanu, syna Jerocháma, syna Elíela, syna Tóacha,",
			"20": "<sup>20</sup> syna Cúfa, syna Elkanu, syna Machata, syna Amasaja,",
			"21": "<sup>21</sup> syna Elkanu, syna Joela, syna Azarju, syna Cefanju,",
			"22": "<sup>22</sup> syna Tachata, syna Assíra, syna Ebjasáfa, syna Koracha,",
			"23": "<sup>23</sup> syna Jishára, syna Koháta, syna Léviho, syna Izraela.",
			"24": "<sup>24</sup> Jeho brat Asáf mu stál po pravici, bol synom Berechju, syna Šimeu,",
			"25": "<sup>25</sup> syna Michaela, syna Baaseju, syna Malkiju,",
			"26": "<sup>26</sup> syna Etniho, syna Zeracha, syna Adaju,",
			"27": "<sup>27</sup> syna Etána, syna Zimmu, syna Šimeiho,",
			"28": "<sup>28</sup> syna Jachata, syna Geršóma, syna Léviho.",
			"29": "<sup>29</sup> Merariho synovia, ich brata, stáli po ľavici: Etán, syn Kišiho, syna Abdiho, syna Mallúcha,",
			"30": "<sup>30</sup> syna Chašabju, syna Amacju, syna Chilkiju,",
			"31": "<sup>31</sup> syna Amciho, syna Baniho, syna Šemera,",
			"32": "<sup>32</sup> syna Machliho, syna Mušiho, syna Merariho, syna Léviho.",
			"33": "<sup>33</sup> Ich bratia leviti boli určení na rozličnú službu v príbytku Božieho domu.",
			"34": "<sup>34</sup> Áron a jeho synovia zapaľovali obetu na oltári na spaľované obety a pálili tymian na kadidlovom oltári. Konali vo veľsvätyni všetky obrady na zmierenie pre Izrael, presne tak, ako to prikázal Boží služobník Mojžiš.",
			"35": "<sup>35</sup> Toto sú Áronovi synovia: jeho syn Eleazár, jeho syn Pinchás, jeho syn Abišúa,",
			"36": "<sup>36</sup> jeho syn Bukki, jeho syn Uzzi, jeho syn Zerachja,",
			"37": "<sup>37</sup> jeho syn Merajót, jeho syn Amarja, jeho syn Achitúb,",
			"38": "<sup>38</sup> jeho syn Cadók, jeho syn Achimaac.",
			"39": "<sup>39</sup> Toto sú ich sídla pri stanových príbytkoch na ich území: Losovaním pripadli kohátovskému rodu Áronovcov:",
			"40": "<sup>40</sup> Hebron v Judsku s príslušnými pastvinami.",
			"41": "<sup>41</sup> Mestské pole však s priľahlými osadami dali Kalébovi, synovi Jefunneho.",
			"42": "<sup>42</sup> Ďalej dali synom Áronovým útočiskové mestá: Hebron a Libnu s príslušnými pastvinami, Jattír, Eštemou s príslušnými pastvinami,",
			"43": "<sup>43</sup> Chiléz s príslušnými pastvinami, Debír s príslušnými pastvinami,",
			"44": "<sup>44</sup> Ašán s príslušnými pastvinami, Bét-Šemeš s príslušnými pastvinami.",
			"45": "<sup>45</sup> Od kmeňa Benjamín im pripadla Geba s príslušnými pastvinami, Alemet s príslušnými pastvinami, Anatót s príslušnými pastvinami. Všetkých miest, ktoré pripadli ich rodom, bolo trinásť.",
			"46": "<sup>46</sup> Ostatné rody Kohátovcov dostali lósom desať miest z polovice kmeňa Menašše.",
			"47": "<sup>47</sup> Geršómovcom pre ich rody pripadlo z kmeňov Jissachár, Ašér, Naftali a z kmeňa Menašše v Bášane trinásť miest.",
			"48": "<sup>48</sup> Pre rody Merariovcov pripadlo lósom z kmeňov Rúben, Gád a Zebulún dvanásť miest.",
			"49": "<sup>49</sup> Tak dali Izraeliti Léviovcom mestá s príslušnými pastvinami.",
			"50": "<sup>50</sup> Tieto zvlášť vyznačené mestá im lósom pridelili z kmeňov Júda, Šimeón a Benjamín.",
			"51": "<sup>51</sup> Príslušníci kohátovských rodov mali mestá na území kmeňa Efrajim.",
			"52": "<sup>52</sup> Pridelili im útočiskové mestá Síchem s príslušnými pastvinami v Efrajimskom pohorí, Gezer s príslušnými pastvinami,",
			"53": "<sup>53</sup> Jokmeám s príslušnými pastvinami, Bét-Chorón s príslušnými pastvinami,",
			"54": "<sup>54</sup> Ajalón s príslušnými pastvinami, Gat-Rimmón s príslušnými pastvinami.",
			"55": "<sup>55</sup> Z polovice kmeňa Menašše dostali ostatné rody Kohátovcov Anér s príslušnými pastvinami a Bilám s príslušnými pastvinami.",
			"56": "<sup>56</sup> Geršómovské rody dostali od druhej polovice kmeňa Menašše Golán v Bášane s príslušnými pastvinami a Aštarót s príslušnými pastvinami,",
			"57": "<sup>57</sup> od kmeňa Jissachár Kedeš s príslušnými pastvinami, Daberat s príslušnými pastvinami,",
			"58": "<sup>58</sup> Ramót s príslušnými pastvinami a Aném s príslušnými pastvinami,",
			"59": "<sup>59</sup> od kmeňa Ašér Mašál s príslušnými pastvinami, Abdón s príslušnými pastvinami,",
			"60": "<sup>60</sup> Chukók s príslušnými pastvinami a Rechób s príslušnými pastvinami,",
			"61": "<sup>61</sup> od kmeňa Naftali: Kedeš v Galilei s príslušnými pastvinami, Chammón s príslušnými pastvinami a Kirjatajim s príslušnými pastvinami.",
			"62": "<sup>62</sup> Ostatní Merariovci dostali od kmeňa Zebulún Rimmón s príslušnými pastvinami a Tabór s príslušnými pastvinami.",
			"63": "<sup>63</sup> Za Jordánom pri Jerichu, východne od Jordánu, od kmeňa Rúben dostali: Becer na púšti s príslušnými pastvinami, Jahcu s príslušnými pastvinami,",
			"64": "<sup>64</sup> Kedemót s príslušnými pastvinami a Mefaat s príslušnými pastvinami,",
			"65": "<sup>65</sup> od kmeňa Gád: Ramót-Gileád s príslušnými pastvinami, Machanajim s príslušnými pastvinami,",
			"66": "<sup>66</sup> Chešbón s príslušnými pastvinami a Jazér s príslušnými pastvinami."
		},
		"7": {
			"1": "<sup>1</sup> Jissachárovi synovia: Tola, Puvva, Jašúb a Šimrón, štyria.",
			"2": "<sup>2</sup> Tolaovi synovia: Uzzi, Refaja, Jeríel, Jachmaj, Jibsám a Šemuel, predáci tolaovských rodín, udatní hrdinovia podľa svojich rodov. Za čias Dávida bol ich počet dvadsaťdvatisícšesťsto mužov.",
			"3": "<sup>3</sup> Uzziho syn: Jizrachja. Jizrachjovi synovia: Michael, Obadja, Joel a Jiššija, piati, všetko predáci.",
			"4": "<sup>4</sup> K nim patrili podľa ich rodov a rodín bojové vojenské oddiely, ktoré mali tridsaťšesťtisíc mužov. Mali totiž mnoho žien a synov.",
			"5": "<sup>5</sup> Ich bratov zo všetkých Jissachárových rodov a do rodových zoznamov bolo zapísaných osemdesiatsedemtisíc udatných hrdinov.",
			"6": "<sup>6</sup> Benjamínovi synovia: Bela, Becher a Jedíael, traja.",
			"7": "<sup>7</sup> Belovi synovia: Ecbón, Uzzi, Uzzíel, Jerimót a Iri, päť rodinných predákov a udatných hrdinov. Rodový zoznam o nich udával dvadsaťdvatisíctridsaťštyri mužov.",
			"8": "<sup>8</sup> Becherovi synovia: Zemira, Joáš, Eliezer, Eljoenaj, Omri, Jeremót, Abija, Anatót a Alemet. Tí všetci sú synmi Bechera.",
			"9": "<sup>9</sup> Zapísaných do rodových zoznamov, predákov rodín a udatných hrdinov bolo dvadsaťtisícdvesto mužov.",
			"10": "<sup>10</sup> Jedíaelov syn: Bilhán. Bilhánovi synovia: Jeúš, Benjamín, Ehúd, Kenaana, Zetán, Taršíš a Achišachar.",
			"11": "<sup>11</sup> Títo všetci sú Jedíaelovi synovia, predáci rodov, sedemnásťtisícdvesto udatných hrdinov, pripravených zasiahnuť do boja.",
			"12": "<sup>12</sup> Írovi synovia: Šuppím a Chuppím. Synom Achéra bol Chuším.",
			"13": "<sup>13</sup> Naftaliho synovia: Jachcíel, Guni, Jecer a Šallúm, vnuci Bilhy.",
			"14": "<sup>14</sup> Menaššeho syn: Asríel, ktorého porodila jeho aramejská vedľajšia žena. Porodila Machíra, otca Gileáda.",
			"15": "<sup>15</sup> Machír oženil Chuppíma a Šuppíma. Jeho sestra sa volala Maacha a druhý syn sa volal Celofchád. Celofchád mal iba dcéry.",
			"16": "<sup>16</sup> Machírova žena Maacha porodila syna a dala mu meno Pereš, jeho brat sa volal Šereš a jeho synovia Ulám a Rekem.",
			"17": "<sup>17</sup> Ulámov syn bol Bedán. To sú synovia Gileáda, syna Machíra, syna Menaššeho.",
			"18": "<sup>18</sup> Jeho sestra Hammolechet porodila Išhóda, Abíezera a Machlu.",
			"19": "<sup>19</sup> Šemidovi synovia: Achján, Šechem, Likchi a Aniám.",
			"20": "<sup>20</sup> Efrajimovi synovia: Šutelach, jeho syn Bered, jeho syn Tachat, jeho syn Elada, jeho syn Tachat,",
			"21": "<sup>21</sup> jeho syn Zabád, jeho syn Šutelach, Ezer a Elád. Povraždili ich však domáci muži z Gatu, lebo sa dohrnuli, aby ich obrali o stáda.",
			"22": "<sup>22</sup> Ich otec Efrajim dlho smútil a bratia ho prišli potešiť.",
			"23": "<sup>23</sup> Nato vošiel k svojej žene, ktorá počala a porodila syna, ktorému dal meno Bería, lebo jeho dom stihlo nešťastie.",
			"24": "<sup>24</sup> Jeho dcéra bola Šeera; vybudovala Dolný i Horný Bét-Chorón a Uzzén-Šeeru.",
			"25": "<sup>25</sup> Jeho syn bol Refach a Rešef, jeho syn Telach, jeho syn Tachan,",
			"26": "<sup>26</sup> jeho syn Ladán, jeho syn Ammichúd, jeho syn Elišama,",
			"27": "<sup>27</sup> jeho syn Nún, jeho syn Jozua.",
			"28": "<sup>28</sup> Ich majetok a sídla boli: Bétel s priľahlými osadami, na východe Naarán, na západe Gezer s priľahlými osadami, Síchem s priľahlými osadami až po Ajju s priľahlými osadami.",
			"29": "<sup>29</sup> Menaššeovci mali Bét-Šeán a jeho osady, Taanach a jeho osady, Megiddo a jeho osady, Dór a jeho osady. V nich bývali potomkovia Jozefa, Izraelovho syna.",
			"30": "<sup>30</sup> Ašérovi synovia: Jimna, Jišva, Jišvi a Bería. Serach bola ich sestrou.",
			"31": "<sup>31</sup> Beríovi synovia: Cheber a Malkíel, ktorý bol otcom Birzajitu.",
			"32": "<sup>32</sup> Cheber splodil Jafléta, Šoméra a Chotáma a ich sestru Šuu.",
			"33": "<sup>33</sup> Jaflétovi synovia: Pasach, Bimhál a Ašvát. To sú Jaflétovi synovia.",
			"34": "<sup>34</sup> Šemerovi synovia: Achi, Rohga, Jechubba a Aram.",
			"35": "<sup>35</sup> Synovia jeho brata Helema: Cofach, Jimna, Šéleš a Amál.",
			"36": "<sup>36</sup> Cofachovi synovia: Súach, Charnefer, Šual, Beri, Jimra,",
			"37": "<sup>37</sup> Becer, Hód, Šamma, Šilša, Jitrán a Beera.",
			"38": "<sup>38</sup> Jeterovi synovia: Jefunne, Pispa a Ara.",
			"39": "<sup>39</sup> Ullovi synovia: Arach, Channíel a Ricja.",
			"40": "<sup>40</sup> Tí všetci boli Ašérovi synovia, predáci rodín, vyberaní udatní hrdinovia, popredné kniežatá. Podľa rodového zoznamu počet ozbrojencov do vojny bol dvadsaťšesťtisíc mužov."
		},
		"8": {
			"1": "<sup>1</sup> Benjamín splodil Belu, svojho prvorodenca, Ašbéla druhého, Achracha tretieho,",
			"2": "<sup>2</sup> Nochu štvrtého a Rafu piateho.",
			"3": "<sup>3</sup> Belovi synovia boli: Addár, Gera, Abihúd,",
			"4": "<sup>4</sup> Abišúa, Naamán, Achóach,",
			"5": "<sup>5</sup> Gera, Šefufán a Churám.",
			"6": "<sup>6</sup> Toto sú Echúdovi synovia, predáci rodín obyvateľov Geby, ktorí ich presťahovali do Manachatu:",
			"7": "<sup>7</sup> Naamán, Achija a Gera, ktorý ich presťahoval a splodil Uzzu a Achichúda.",
			"8": "<sup>8</sup> Šacharajim splodil synov na moábskom poli po prepustení žien Chuším a Baary.",
			"9": "<sup>9</sup> S Chodeš, svojou ženou, splodil Jobába, Cibju, Mešu, Malkáma,",
			"10": "<sup>10</sup> Jeúca, Sachju a Mirmu. To boli jeho synovia, predáci rodín.",
			"11": "<sup>11</sup> S Chuším splodil Abitúba a Elpaala.",
			"12": "<sup>12</sup> Elpaalovi synovia: Eber, Mišeám a Šemed, ktorý vybudoval Óno a Lód s priľahlými osadami.",
			"13": "<sup>13</sup> Bería a Šema boli predákmi rodín obyvateľov Ajalónu. Tí zahnali na útek obyvateľov Gatu.",
			"14": "<sup>14</sup> Achjo, Šašák, Jeremót,",
			"15": "<sup>15</sup> Zebadja, Arád, Éder,",
			"16": "<sup>16</sup> Michael, Jišpa a Jocha boli synmi Beríu.",
			"17": "<sup>17</sup> Zebadja, Mešullám, Chizki, Cheber,",
			"18": "<sup>18</sup> Jišmeraj, Jizlía a Jobáb boli synmi Elpaala.",
			"19": "<sup>19</sup> Jakím, Zichri, Zabdi,",
			"20": "<sup>20</sup> Elíenaj, Cilletaj, Elíel,",
			"21": "<sup>21</sup> Adaja, Beraja a Šimrát boli Šimeiho synovia.",
			"22": "<sup>22</sup> Jišpán, Eber, Elíel,",
			"23": "<sup>23</sup> Abdón, Zichri, Chanán,",
			"24": "<sup>24</sup> Chananja, Elám, Antotija,",
			"25": "<sup>25</sup> Jifdeja a Penuel boli Šašákovi synovia.",
			"26": "<sup>26</sup> Šamšeraj, Šecharja, Atalja,",
			"27": "<sup>27</sup> Jaarešja, Elija a Zichri boli Jerochámovi synovia.",
			"28": "<sup>28</sup> Títo boli predáci rodín a podľa svojich rodov ako predáci bývali v Jeruzaleme.",
			"29": "<sup>29</sup> V Gibeóne bývali: otec Gibeónu Jeíel a jeho žena menom Maacha.",
			"30": "<sup>30</sup> Jeho prvorodený syn bol Abdón a ďalší: Cúr, Kíš, Baal, Nadáb,",
			"31": "<sup>31</sup> Gedór, Achjo, Zecher",
			"32": "<sup>32</sup> a Miklót, ktorý splodil Šimeu. Aj tí bývali u svojich bratov v Jeruzaleme, a to oproti nim.",
			"33": "<sup>33</sup> Nér splodil Kíša, Kíš splodil Šaula, Šaul splodil Jonatána, Malki-Šuu, Abinadába a Ešbaala.",
			"34": "<sup>34</sup> Jonatánov syn bol Meríb-Baal, ktorý splodil Michu.",
			"35": "<sup>35</sup> Michovi synovia boli: Pitón, Melech, Taréa a Acház.",
			"36": "<sup>36</sup> Acház splodil Joaddu, Joadda splodil Alemeta, Azmáveta a Zimriho a Zimri splodil Mocu.",
			"37": "<sup>37</sup> Moca splodil Binu, jeho syn bol Rafa, jeho syn Elasa, jeho syn Acél.",
			"38": "<sup>38</sup> Acél mal šesť synov. Ich mená sú: Azrikám, Bochru, Jišmael, Šearja, Obadja a Chanán. Všetci tí boli Acélovi synovia.",
			"39": "<sup>39</sup> Synovia jeho brata Éšeka: jeho prvorodený Ulám, druhý Jeúš, tretí Elifelet.",
			"40": "<sup>40</sup> Ulámovi synovia boli udatní hrdinovia, lukostrelci. Mali mnoho synov a vnukov — stopäťdesiat. Tí všetci pochádzali z Benjamínovcov."
		},
		"9": {
			"1": "<sup>1</sup> Všetci Izraeliti sa dali zapísať do rodového zoznamu a dostali sa do Knihy izraelských a judských kráľov. Pre ich nevernosť ich odvliekli do Babylonu.",
			"2": "<sup>2</sup> Prvými usadlíkmi, čo bývali na svojom vlastníctve vo svojich mestách, boli niektorí Izraeliti, kňazi, leviti a chrámoví nevoľníci.",
			"3": "<sup>3</sup> V Jeruzaleme bývali niektorí Júdovci, Benjamínovci, Efrajimovci a Menaššeovci:",
			"4": "<sup>4</sup> Utaj, syn Ammichúda, syna Omriho, syna Imriho, syna Baniho, z potomkov Pereca, Júdovho syna.",
			"5": "<sup>5</sup> Zo Šilčanov: prvorodený Asaja a jeho synovia.",
			"6": "<sup>6</sup> Zo Zerachovcov: Jeuel a ich bratia, šesťstodeväťdesiat mužov.",
			"7": "<sup>7</sup> Z Benjamínovcov: Sallu, syn Mešulláma, syna Hodavju, syna Hassenuu,",
			"8": "<sup>8</sup> ďalej Jibneja, syn Jerocháma, Ela, syn Uzziho, syna Michriho, Mešullám, syn Šefatju, syna Reuela, syna Jibniju",
			"9": "<sup>9</sup> a ich bratia podľa rodokmeňa, deväťstopäťdesiatšesť mužov. Všetci títo muži boli predákmi rodín.",
			"10": "<sup>10</sup> Z kňazov: Jedaja, Jojaríb, Jachín,",
			"11": "<sup>11</sup> Azarja, syn Chilkiju, syna Mešulláma, syna Cadóka, syna Merajóta, syna Achitúba, predstaveného Božieho domu.",
			"12": "<sup>12</sup> Adaja, syn Jerocháma, syna Pašchúra, syna Malkiju, Maasaj, syn Adíela, syna Jachzeru, syna Mešulláma, syna Mešillemíta, syna Imméra",
			"13": "<sup>13</sup> a ich bratia, predáci rodín, tisícsedemstošesťdesiat schopných mužov na výkon služby v Božom dome.",
			"14": "<sup>14</sup> Z levitov: Šemaja, syn Chaššúba, syna Azrikáma, syna Chašabju z Merariovcov.",
			"15": "<sup>15</sup> Bakbakkar, Chereš, Galál, Mattanja, syn Michu, syna Zichriho, syna Asáfa,",
			"16": "<sup>16</sup> Obadja, syn Šemaju, syna Galála, syna Jedutúna, Berechja, syn Asu, syna Elkanu, ktorý býval v netofských dedinách.",
			"17": "<sup>17</sup> Vrátnici: Šallúm, Akkúb, Talmón, Achiman a ich bratia, Šallúm bol predákom.",
			"18": "<sup>18</sup> Dosiaľ stráži pri východnej kráľovskej bráne. To sú vrátnici z táborov Léviovcov.",
			"19": "<sup>19</sup> Šallúm, syn Koreho, syna Ebjasáfa, syna Koracha a bratia z jeho rodiny, Korachovci, mali na starosti strážnu službu pri prahoch stanu. Ich otcovia boli dozorcami pri vchode do Hospodinovho tábora.",
			"20": "<sup>20</sup> Eleazárov syn Pinchás bol kedysi ich predstaveným a Hospodin bol s ním.",
			"21": "<sup>21</sup> Mešelemjov syn Zecharja bol vrátnikom pri vchode do stanu stretávania.",
			"22": "<sup>22</sup> Všetkých tých, čo boli vybraní za vrátnikov prahov, bolo dvestodvanásť. Do rodového zoznamu sa dali zapísať vo svojich dedinách. Dávid a videc Samuel ich uviedli do úradu.",
			"23": "<sup>23</sup> Oni a ich synovia konali strážnu službu pri bránach Hospodinovho domu, stanového príbytku.",
			"24": "<sup>24</sup> Vrátnici boli na štyroch stranách: na východe, na západe, na severe a na juhu.",
			"25": "<sup>25</sup> V určitý čas sa mali ich bratia po dedinách dostaviť na sedem dní k nim,",
			"26": "<sup>26</sup> pretože títo štyria hlavní vrátnici boli stále zaujatí povinnosťou. Niektorí leviti spravovali komory a pokladnice Božieho domu.",
			"27": "<sup>27</sup> Nocúvali v okolí Božieho domu, pretože im patrila strážna služba a každé ráno museli otvárať.",
			"28": "<sup>28</sup> Niektorí mali na starosti bohoslužobné náčinie, mali ho priniesť presne toľko, koľko ho odniesli.",
			"29": "<sup>29</sup> Iní zas dozerali na rôzny riad a sväté predmety, na jemnú múku, na víno a olej, na kadidlo a vonné balzamy.",
			"30": "<sup>30</sup> Niektorí z kňazov miešali masti z balzamov.",
			"31": "<sup>31</sup> Levita Mattitja, prvorodený Korachovca Šallúma, dostal poverenie na výrobu pečiva.",
			"32": "<sup>32</sup> Podaktorí Kohátovci, ich bratia, mali na starosti prípravu predkladaných chlebov, ktoré sa mali vymieňať každú sobotu.",
			"33": "<sup>33</sup> Toto sú speváci, predáci levitských rodín. Bývali v komorách, oslobodení od iných povinností, lebo vo dne v noci museli zotrvať v práci.",
			"34": "<sup>34</sup> Títo boli predákmi levitských rodín podľa svojich rodov a ako takí bývali v Jeruzaleme.",
			"35": "<sup>35</sup> V Gibeóne býval otec Gibeónu Jeíel, ktorého žena sa volala Maacha,",
			"36": "<sup>36</sup> jeho prvorodený syn bol Abdón, ďalej Cúr, Kíš, Baal, Nér, Nadáb,",
			"37": "<sup>37</sup> Gedór, Achjo, Zecharja a Miklót.",
			"38": "<sup>38</sup> Miklót splodil Šimeáma. Aj oni bývali pri svojich bratoch v Jeruzaleme, a to oproti nim.",
			"39": "<sup>39</sup> Nér splodil Kíša, Kíš splodil Šaula, Šaul splodil Jonatána, Malki-Šuu, Abinadába a Ešbaala.",
			"40": "<sup>40</sup> Jonatánov syn bol Meríb-Baal a Meríb-Baal splodil Michu.",
			"41": "<sup>41</sup> Michovi synovia: Pitón, Melech, Tachrea a Acház.",
			"42": "<sup>42</sup> Acház splodil Jaru, Jara splodil Alemeta, Azmáveta a Zimriho. Zimri splodil Mocu,",
			"43": "<sup>43</sup> Moca splodil Binu. Jeho syn bol Refaja, jeho syn Elasa, jeho syn Acél.",
			"44": "<sup>44</sup> Acél mal šesť synov. Toto sú ich mená: Azrikám, Bochru, Jišmael, Šearja, Obadja a Chanán. To boli Acélovi synovia."
		},
		"10": {
			"1": "<sup>1</sup> Filištínci bojovali proti Izraelu. Izraeliti sa dali pred Filištíncami na útek a ranení popadali na vrchu Gilbóa.",
			"2": "<sup>2</sup> Filištínci pátrali po Šaulovi a po jeho synoch a zabili mu synov Jonatána, Abinadába a Malki-Šuu.",
			"3": "<sup>3</sup> Keď sa vyostril útok proti Šaulovi, prekvapili ho lukostrelci a zranili.",
			"4": "<sup>4</sup> Vtedy prikázal Šaul svojmu zbrojnošovi: „Vytas meč a prebodni ma ním, aby ma títo neobrezanci, keď prídu, nezneuctili.“ Zbrojnoš sa však zdráhal, lebo sa veľmi bál. Nato sa meča chopil Šaul a nabodol sa naň.",
			"5": "<sup>5</sup> Keď jeho zbrojnoš videl, že Šaul je mŕtvy, nabodol sa na meč aj on a zomrel.",
			"6": "<sup>6</sup> Zahynul Šaul, jeho traja synovia, celý jeho dom; zahynuli súčasne.",
			"7": "<sup>7</sup> Keď všetci Izraeliti, čo bývali v údolí, videli, že bojovníci utekajú a že zahynul Šaul a jeho synovia, opustili svoje mestá a ušli. Nato prišli Filištínci a usadili sa v nich.",
			"8": "<sup>8</sup> Na druhý deň prišli Filištínci okradnúť pobitých a našli Šaula i jeho synov, padlých na vrchu Gilbóa.",
			"9": "<sup>9</sup> Vyzliekli ho, vzali jeho hlavu i výzbroj a dali kolovať po filištínskej krajine, aby s novinou oboznámili svoje modly a ľud.",
			"10": "<sup>10</sup> Jeho výzbroj uložili v dome svojho boha a jeho lebku nastokli v Dagónovom dome.",
			"11": "<sup>11</sup> Keď všetci obyvatelia Jabéš-Gileádu počuli, čo všetko urobili Filištínci Šaulovi,",
			"12": "<sup>12</sup> pobrali sa všetci odvážni, vzali mŕtve telo Šaula i jeho synov a preniesli ich do Jabéša. Ich pozostatky pochovali v Jabéši pod cerom a postili sa sedem dní.",
			"13": "<sup>13</sup> Tak skonal Šaul pre svoju nevernosť voči Hospodinovi, lebo nezachovával jeho slovo, a aj preto, že sa domáhal rady u ducha zosnulého.",
			"14": "<sup>14</sup> Keďže nehľadal radu u Hospodina, vydal ho na smrť a kráľovstvo previedol na Izajovho syna Dávida."
		},
		"11": {
			"1": "<sup>1</sup> Celý Izrael sa zišiel k Dávidovi do Hebronu a vyhlásil: „Sme predsa tvoja kosť a tvoje telo.",
			"2": "<sup>2</sup> Ty si viedol Izrael pri výprave i návrate aj predtým, než sa stal Šaul kráľom, aj vtedy, keď kraľoval. Hospodin, tvoj Boh, ti povedal: ‚Ty budeš spravovať môj izraelský ľud a staneš sa jeho vojvodcom.‘“",
			"3": "<sup>3</sup> Všetci starší Izraela prišli ku kráľovi do Hebronu, kde s nimi uzavrel Dávid pred Hospodinom zmluvu. Nato pomazali Dávida za kráľa nad Izraelom podľa Hospodinovho slova, vyrieknutého Samuelom.",
			"4": "<sup>4</sup> Dávid sa s celým Izraelom odobral k Jeruzalemu, ktorý sa predtým volal Jebús; jeho územie obývali Jebúseji.",
			"5": "<sup>5</sup> Obyvatelia Jebúsu povedali Dávidovi: „Sem sa nedostaneš!“ Dávid však dobyl pevnosť Sion, to je Dávidovo mesto.",
			"6": "<sup>6</sup> Vtedy povedal Dávid: „Kto prvý napadne Jebúsejov, stane sa hlavným veliteľom. Prvý vystúpil Cerujin syn Joáb a stal sa hlavným veliteľom.“",
			"7": "<sup>7</sup> Dávid sa usídlil v pevnosti, preto mesto nazvali podľa Dávida.",
			"8": "<sup>8</sup> Dookola od Milló až po obvodové múry zbudoval mesto. Ostatok mesta obnovil Joáb.",
			"9": "<sup>9</sup> Tak sa Dávid čoraz viac vzmáhal a Hospodin zástupov bol s ním.",
			"10": "<sup>10</sup> Toto sú hlavní hrdinovia Dávida, ktorí sa spolu s celým Izraelom zasadili o to, aby ho uviedli do hodnosti kráľa nad Izraelom podľa Hospodinovho slova o Izraeli.",
			"11": "<sup>11</sup> Toto je zoznam Dávidových hrdinov: Jašobám, Chachmoniho syn, predák z trojice hrdinov. Ten sa oháňal kopijou proti trom stovkám mužov, ktorých prebodol v jednom boji.",
			"12": "<sup>12</sup> Ďalší bol Achóachovec Eleazár, Dodov syn. Patril k trojici hrdinov.",
			"13": "<sup>13</sup> On bol s Dávidom v Pas-Dammíme, keď sa tam Filištínci zhromažďovali na boj. Bolo tam pole, obsiate jačmeňom. Keď ľud utekal pred Filištíncami,",
			"14": "<sup>14</sup> on sa postavil do stredu toho poľa, uhájil ho, a vtedy Izraeliti porazili Filištíncov. Tak dopomohol Hospodin k veľkému víťazstvu.",
			"15": "<sup>15</sup> Raz prišli traja z tridsiatich predákov k Dávidovi na skalu pri jaskyni Adullám. Filištínske vojsko vtedy táborilo v údolí Refajov.",
			"16": "<sup>16</sup> Dávid bol práve v pevnosti, keď sa filištínska posádka nachádzala v Betleheme.",
			"17": "<sup>17</sup> Tu zachvátila Dávida žiadosť a povedal: „Kto ma napojí vodou zo studne, ktorá je v Betleheme pri bráne?“",
			"18": "<sup>18</sup> Tí traja sa prebojovali filištínskym táborom, načreli vody zo studne, ktorá je v bráne Betlehema, vzali ju a priniesli Dávidovi. Dávid ju však nechcel piť, ale vylial ju ako obetu Hospodinovi.",
			"19": "<sup>19</sup> Povedal: „Nech ma Boh chráni, aby som to urobil! Mám azda piť krv týchto mužov? Veď s nasadením vlastných životov ju priniesli.“ A nechcel ju piť. To vykonali tí traja hrdinovia.",
			"20": "<sup>20</sup> Joábov brat Abšaj bol predákom medzi tridsiatimi. Oháňal sa kopijou proti trom stovkám mužov, ktorých pobil, a bol povestný medzi tridsiatimi.",
			"21": "<sup>21</sup> Medzi tridsiatimi požíval úctu a bol ich vodcom, no oným trom sa nevyrovnal.",
			"22": "<sup>22</sup> Jojadov syn Benaja, udatný muž, bohatý na skutky, pochádzal z Kabceelu. On zabil dvoch moábskych hrdinov. Jedného dňa, keď snežilo, zišiel do jamy a zabil leva.",
			"23": "<sup>23</sup> Zabil aj ozrutného Egypťana, vysokého päť lakťov. Hoci mal Egypťan v ruke kopiju ako tkáčsky návoj, prišiel k nemu len s palicou, vytrhol mu kopiju z ruky a zabil ho ňou.",
			"24": "<sup>24</sup> To vykonal Jojadov syn Benaja a preslávil sa tak medzi tridsiatimi hrdinami.",
			"25": "<sup>25</sup> Medzi tridsiatimi požíval úctu, ale onej trojici sa nevyrovnal. Dávid ho ustanovil za veliteľa svojej telesnej stráže.",
			"26": "<sup>26</sup> Ďalšími udatnými hrdinami boli: Joábov brat Asael, Dodov syn Elchanán z Betlehema,",
			"27": "<sup>27</sup> Šammót z Haróru, Chelec z Pelónu,",
			"28": "<sup>28</sup> Ikkéšov syn Ira z Tekóy, Abíezer z Anatótu,",
			"29": "<sup>29</sup> Sibbechaj z Chuše, Achóachovec Ilaj,",
			"30": "<sup>30</sup> Mahraj z Netófy, Baanov syn Cheled z Netofy,",
			"31": "<sup>31</sup> Ribajov syn Itaj z benjamínskej Gibey, Benaja z Piratónu,",
			"32": "<sup>32</sup> Churaj z Nachale-Gaašu, Abíel z Araby,",
			"33": "<sup>33</sup> Azmávet z Bachurímu, Eljachba zo Šaalbónu,",
			"34": "<sup>34</sup> Hašémovi synovia z Gizónu, Šageho syn Jonatán z Haráru,",
			"35": "<sup>35</sup> Sachárov syn Achiám z Haráru, Úrov syn Elifal,",
			"36": "<sup>36</sup> Chefer z Mechery, Achija z Pelónu,",
			"37": "<sup>37</sup> Checro z Karmelu, Naaraj, syn Ezbaja,",
			"38": "<sup>38</sup> Joel, brat Nátana, Mibchár, syn Hagriho,",
			"39": "<sup>39</sup> Ammónčan Celek, Nachraj z Berótu, zbrojnoš Cerujinho syna Joába,",
			"40": "<sup>40</sup> jitrijský Ira, jitrijský Garéb,",
			"41": "<sup>41</sup> Chetita Uriáš, Achlajov syn Zabád,",
			"42": "<sup>42</sup> Rúbenovec Adina, syn Šizu, rúbenovský predák nad tridsiatimi,",
			"43": "<sup>43</sup> Chanán, syn Maachu, mitnijský Jošafát,",
			"44": "<sup>44</sup> Uzzija z Aštarótu, Šama a Jeíel, synovia Chotáma z Aroéru,",
			"45": "<sup>45</sup> Jedíael, syn Šimriho a jeho brat Jocha z Tícu,",
			"46": "<sup>46</sup> Elíel z Machavímu, Elnaamovi synovia Jeribaj a Jošavja, Moábčan Jitma,",
			"47": "<sup>47</sup> Elíel, Obéd a Jaasíel z Coby."
		},
		"12": {
			"1": "<sup>1</sup> Títo prišli k Dávidovi do Ciklagu, keď ešte nemal prístup k Šaulovi, Kíšovmu synovi. Patrili k jeho hrdinom a spolubojovníkom.",
			"2": "<sup>2</sup> Boli vyzbrojení lukom, schopní pravicou i ľavicou hádzať kamene a strieľať šípy z luku. Pochádzali zo Šaulových rodákov Benjamínovcov:",
			"3": "<sup>3</sup> predák Achíezer a Joáš, synovia Šemau z Gibey, Jezíel a Pelet, synovia Azmáveta, Beracha a Jehu z Anatótu,",
			"4": "<sup>4</sup> Jišmaja z Gibeónu, hrdina medzi tridsiatimi a ich veliteľ,",
			"5": "<sup>5</sup> Jirmeja, Jachazíel, Jochanán, Jozabád z Gedery,",
			"6": "<sup>6</sup> Eluzaj, Jerimót, Bealja, Šemarja, Šefatja z Charífu,",
			"7": "<sup>7</sup> Elkana, Jiššija, Azarel, Joezer, Jašobám, Korachovci,",
			"8": "<sup>8</sup> Joela, Zebadja, synovia Jerocháma z Gedóru.",
			"9": "<sup>9</sup> Z Gádovcov prešli k Dávidovi do pevnosti na púšť udatní hrdinovia, osvedčení bojovníci, vyzbrojení štítom a kopijou. Vyzerali ako lev a boli rýchli ako gazely na vrchoch.",
			"10": "<sup>10</sup> Predákom bol Ezer, Obadja bol druhý, Eliáb tretí,",
			"11": "<sup>11</sup> Mišmanna štvrtý, Jirmeja piaty,",
			"12": "<sup>12</sup> Attaj šiesty, Elíel siedmy,",
			"13": "<sup>13</sup> Jochanán ôsmy, Elzabád deviaty,",
			"14": "<sup>14</sup> Jirmeja desiaty, Machbannaj jedenásty.",
			"15": "<sup>15</sup> Títo Gádovci boli veliteľmi vojska. Slabší velil sto vojakom, schopnejší celému pluku.",
			"16": "<sup>16</sup> To oni prešli cez Jordán v prvom mesiaci, keď zaplavoval oba brehy, a rozohnali všetkých obyvateľov dolín na východ a na západ.",
			"17": "<sup>17</sup> K Dávidovi do pevnosti prišli i niektorí Benjamínovci a Júdovci.",
			"18": "<sup>18</sup> Dávid ich uvítal a vyhlásil: „Ak prichádzate ku mne ako priatelia pomôcť mi, rád sa s vami spojím. Ak ste ma však prišli zradiť nepriateľom, hoci mi nelipne na rukách násilie, nech na to zhliadne Boh našich otcov a potrestá to.“",
			"19": "<sup>19</sup> Vtedy duch vnukol Amasajovi, veliteľovi tridsiatich, slová: „Sme tvoji, Dávid, sme s tebou, syn Izajov. Pokoj, pokoj tebe, i tomu, kto ti pomáha! Veď sám tvoj Boh ti pomáha.“ Nato ich Dávid prijal a urobil veliteľmi bojových jednotiek.",
			"20": "<sup>20</sup> Niektorí Menaššeovci sa pripojili k Dávidovi, keď vyšiel spolu s Filištíncami do boja proti Šaulovi. Nepomáhali im však, lebo ho filištínske kniežatá po porade odvolali s odôvodnením: „Mohol by prebehnúť k Šaulovi, svojmu pánovi, a my by sme prišli o hlavu.“",
			"21": "<sup>21</sup> Keď odišiel do Ciklagu, pridali sa k nemu Menaššeovci: Adnach, Jozabád, Jedíael, Michael, Jozabád, Elihu, Cilletaj, velitelia tisícov z Menaššeho.",
			"22": "<sup>22</sup> Tí pomáhali Dávidovi v boji proti záškodníkom. Keďže to všetko boli udatní hrdinovia, stali sa veliteľmi vojska.",
			"23": "<sup>23</sup> Deň čo deň prichádzali ľudia podporiť Dávida, až z nich vznikol mohutný tábor, podobný Božiemu táboru.",
			"24": "<sup>24</sup> Toto je zoznam veliteľov vojska, ktorí prišli k Dávidovi do Hebronu, aby podľa Hospodinovho slova naňho preniesli Šaulovo kráľovstvo.",
			"25": "<sup>25</sup> Júdovcov, ktorí nosili štít a kopiju, bolo šesťtisícosemsto do boja vystrojených mužov.",
			"26": "<sup>26</sup> Zo Šimeónovcov: sedemtisícsto udatných bojovníkov.",
			"27": "<sup>27</sup> Z Léviovcov: štyritisícšesťsto,",
			"28": "<sup>28</sup> ďalej vojvodca Áronovcov Jojada a s ním tritisícsedemsto mužov,",
			"29": "<sup>29</sup> Cadók, mladý, ale udatný hrdina a jeho rodina, dvadsaťdva hodnostárov.",
			"30": "<sup>30</sup> Z Benjamínovcov, Šaulových rodákov, tritisíc mužov. Väčšina z nich sa až dovtedy húževnato pridŕžala Šaulovho domu.",
			"31": "<sup>31</sup> Z Efrajimovcov dvadsaťtisícosemsto udatných hrdinov, preslávených vo svojich rodinách.",
			"32": "<sup>32</sup> Z polovice kmeňa Menašše: osemnásťtisíc zvlášť vymenovaných, aby prišli dosadiť Dávida za kráľa.",
			"33": "<sup>33</sup> Z Jissachárovcov, ktorí pochopili, ako sa budú vyvíjať udalosti a vedeli, čo má Izrael robiť: dvesto predákov a pod ich velením všetci ich súkmeňovci.",
			"34": "<sup>34</sup> Zo Zebulúna: päťdesiattisíc bojovníkov, pripravených do boja s rozličnými zbraňami, odhodlaných rázne zasiahnuť.",
			"35": "<sup>35</sup> Z Naftaliho: tisíc veliteľov a s nimi tridsaťsedemtisíc štítom a kopijou vyzbrojených mužov.",
			"36": "<sup>36</sup> Z Dánovcov: dvadsaťosemtisícšesťsto do boja pripravených mužov.",
			"37": "<sup>37</sup> Z Ašéra: štyridsaťtisíc bojovníkov, pripravených do boja.",
			"38": "<sup>38</sup> Spoza Jordánu: z Rúbenovcov, Gádovcov a z polovice kmeňa Menašše stodvadsaťtisíc mužov, vyzbrojených rozličnými druhmi zbraní.",
			"39": "<sup>39</sup> Všetci títo bojovníci usporiadaní do oddielov prišli do Hebronu odhodlane, aby dosadili Dávida za kráľa nad celým Izraelom. Aj všetci ostatní Izraeliti boli jednomyseľne za to, aby nastolili Dávida za kráľa.",
			"40": "<sup>40</sup> Strávili tam pri Dávidovi tri dni, jedli a pili, lebo sa ich bratia o nich postarali.",
			"41": "<sup>41</sup> Aj ich susedia až po Jissachára, Zebulúna a Naftaliho privážali im na osloch, ťavách, muliciach a voloch potraviny: múčne jedlá, lisované figy, sušené hrozienka, víno, olej, hovädzí a drobný statok v hojnosti, lebo v Izraeli zavládla radosť."
		},
		"13": {
			"1": "<sup>1</sup> Dávid mal poradu s tisícnikmi, stotníkmi a všetkými náčelníkmi.",
			"2": "<sup>2</sup> Po nej navrhol celému zhromaždeniu Izraela: „Ak uznáte za dobré a ak je to i vôľa Hospodina, nášho Boha, rozhlásili by sme po posloch ostatným našim bratom v rôznych krajoch Izraela — zároveň i kňazom a levitom na ich pastvinách —, aby sa k nám zhromaždili.",
			"3": "<sup>3</sup> Prenesieme k nám archu nášho Boha, lebo za Šaulových čias sme o ňu nejavili záujem.“",
			"4": "<sup>4</sup> Celé zhromaždenie s tým súhlasilo, lebo ten návrh všetok ľud pokladal za správny.",
			"5": "<sup>5</sup> Dávid zhromaždil celý Izrael od egyptského Šichóru až po oblasť Chamátu, aby preniesli Božiu archu z Kirjat-Jearímu.",
			"6": "<sup>6</sup> Dávid sa vybral s celým Izraelom do Baaly, inak do Kirjat-Jearímu v Judsku, aby stadiaľ vyniesli archu Boha, Hospodina, ktorý sa volá Tróniaci nad cherubmi.",
			"7": "<sup>7</sup> Božiu archu prevážali na novom voze z Abinadábovho domu. Uzza a Achjo riadili voz.",
			"8": "<sup>8</sup> Dávid a celý Izrael tancovali pred Bohom z celej sily za spevov a zvuku citár, hárf, bubnov, cimbalov a trúb.",
			"9": "<sup>9</sup> Keď prišli ku Kidónovmu humnu, Uzza vystrel ruku, aby zadržal archu, lebo jej hrozilo, že ju voly prevrhnú.",
			"10": "<sup>10</sup> Vtedy sa rozhneval Hospodin na Uzzu a zabil ho, lebo vystrel ruku na archu. Zomrel tam pred Bohom.",
			"11": "<sup>11</sup> Dávida zronilo, že Hospodin pretrhol Uzzovi život, preto sa dodnes to miesto nazýva Perec-Uzza.",
			"12": "<sup>12</sup> V ten deň dostal Dávid strach z Boha a povedal: „Ako len dopravíme k sebe Božiu archu?“",
			"13": "<sup>13</sup> Ani ju nedal preniesť k sebe do Dávidovho mesta, ale ju uložil v dome Gatčana Obéd-Edóma.",
			"14": "<sup>14</sup> Tak zostala Božia archa tri mesiace v dome Obéd-Edóma. Hospodin požehnával jeho domácnosť i všetko, čo mal."
		},
		"14": {
			"1": "<sup>1</sup> Týrsky kráľ Chirám vypravil k Dávidovi poslov a poslal i cédrové drevo, murárov, tesárov, aby mu postavili palác.",
			"2": "<sup>2</sup> Potom spoznal Dávid, že ho za kráľa nad Izraelom potvrdil sám Hospodin a jeho kráľovstvo pre jeho ľud Izrael vysoko povzniesol.",
			"3": "<sup>3</sup> V Jeruzaleme si Dávid pribral ďalšie ženy a splodil ďalších synov a dcéry.",
			"4": "<sup>4</sup> Toto sú mená tých, čo sa mu narodili v Jeruzaleme: Šammúa, Šobáb, Nátan, Šalamún,",
			"5": "<sup>5</sup> Jibchár, Elišua, Elpelet,",
			"6": "<sup>6</sup> Nogah, Nefeg, Jafia,",
			"7": "<sup>7</sup> Elišama, Beeljada, Elifelet.",
			"8": "<sup>8</sup> Keď sa Filištínci dopočuli, že bol Dávid pomazaný za kráľa nad celým Izraelom, pobrali sa ho všetci hľadať. Dávid sa to dozvedel a vyrazil proti nim.",
			"9": "<sup>9</sup> Filištínci prišli a plienili v údolí Refajov.",
			"10": "<sup>10</sup> Dávid si pýtal radu u Boha: „Mám napadnúť Filištíncov? Vydáš mi ich do moci?“ Hospodin mu odpovedal: „Len choď, vydám ich do tvojej moci.“",
			"11": "<sup>11</sup> Tak postupovali do Baal-Peracímu, kde ich Dávid porazil. Povedal: „Prostredníctvom mňa prelomil Boh šíky mojich nepriateľov, ako keď voda prelomí hrádzu.“ Preto nazvali to miesto Baal-Peracím.",
			"12": "<sup>12</sup> Zanechali tam i svoje modly, ktoré dal Dávid spáliť.",
			"13": "<sup>13</sup> Filištínci znovu plienili v údolí.",
			"14": "<sup>14</sup> Keď sa Dávid spytoval Boha, dostal od neho odpoveď: „Nechoď za nimi, ale ich obíď a napadni od balzamovníkov.",
			"15": "<sup>15</sup> Len čo začuješ vo vrcholcoch balzamovníkov šuchot krokov, ihneď vyraz do boja, lebo to Boh vyšiel pred tebou poraziť vojsko Filištíncov.“",
			"16": "<sup>16</sup> Dávid poslúchol Boží príkaz a porážal vojsko Filištíncov od Gibeónu až po Gezer.",
			"17": "<sup>17</sup> Povesť o Dávidovi sa rozniesla do všetkých krajín a Hospodin spôsobil, že sa ho báli všetky národy."
		},
		"15": {
			"1": "<sup>1</sup> V Dávidovom meste si Dávid postavil budovy, pripravil pre Božiu archu miesto, roztiahol nad ňou stan",
			"2": "<sup>2</sup> a vyhlásil: „Nikto nesmie nosiť Božiu archu, iba leviti. Ich si totiž vyvolil Hospodin, aby ju nosili a vykonávali pri nej službu navždy.“",
			"3": "<sup>3</sup> Dávid zhromaždil celý Izrael do Jeruzalema, aby vyniesli Hospodinovu archu na miesto, ktoré pre ňu pripravil.",
			"4": "<sup>4</sup> Nato zvolal Dávid Áronovcov a levitov:",
			"5": "<sup>5</sup> z Kohátovcov predstaveného Uríela a stodvadsať jeho bratov,",
			"6": "<sup>6</sup> z Merariovcov predstaveného Asaju a dvestodvadsať jeho bratov,",
			"7": "<sup>7</sup> z Geršómovcov predstaveného Joela a stotridsať jeho bratov,",
			"8": "<sup>8</sup> z Elicafánovcov predstaveného Šemaju a dvesto jeho bratov,",
			"9": "<sup>9</sup> z Chebrónovcov predstaveného Elíela a osemdesiat jeho bratov,",
			"10": "<sup>10</sup> z Uzzíelovcov predstaveného Amminadába a stodvanásť jeho bratov.",
			"11": "<sup>11</sup> Potom povolal Dávid kňazov Cadóka a Ebjatára i levitov Uríela, Asaju, Joela, Šemaju, Elíela a Amminadába",
			"12": "<sup>12</sup> a prikázal im: „Ste predstavenými levitských rodín. Posväťte sa spolu s vašimi bratmi a vyneste archu Hospodina, Boha Izraela, na miesto, ktoré som jej pripravil.",
			"13": "<sup>13</sup> Pretože sa to predošlé dialo bez vás, dopustil na nás Hospodin, náš Boh, pohromu, lebo sme sa nedopytovali na jeho vôľu podľa predpisu.“",
			"14": "<sup>14</sup> Kňazi a leviti sa posvätili a vyniesli archu Hospodina, Boha Izraela.",
			"15": "<sup>15</sup> Leviti niesli Božiu archu, ako prikázal Mojžiš, podľa Hospodinovho slova: so žrďami na pleciach.",
			"16": "<sup>16</sup> Dávid prikázal levitským vodcom, aby určili svojich bratov za spevákov. Mali za sprievodu hudobných nástrojov — hárf, citár a cimbalov — vzbudzovať radostnú náladu.",
			"17": "<sup>17</sup> Leviti ustanovili Hemána, Joelovho syna, a z jeho bratov Asáfa, Berechjovho syna, z Merariovcov, svojich bratov, Etána, Kušajaovho syna,",
			"18": "<sup>18</sup> s nimi ich bratov, ktorí ich mali striedať: Zecharju mladšieho, Jaazíela, Šemiramóta, Jechíela, Unniho, Eliába, Benaju, Maaseju, Mattitju, Eliflehua, Mikneju, Obéd-Edóma, Jeíela, vrátnikov,",
			"19": "<sup>19</sup> spevákov: Hemána, Asáfa a Etána, aby hrali na bronzové cimbaly.",
			"20": "<sup>20</sup> Zecharju, Azíela, Šemiramóta, Jechíela, Unniho, Eliába, Maaseju, Benaju s harfami pre vysoký hlas,",
			"21": "<sup>21</sup> Mattitju, Eliflehua, Mikneju, Obéd-Edóma, Jeíela, Azazju, aby s citarami viedli hlboký hlas.",
			"22": "<sup>22</sup> Kenanja, vedúci levitov, pri nosení mal riadiť prenášanie, lebo sa v tom vyznal.",
			"23": "<sup>23</sup> Berechja a Elkana boli vrátnikmi pri arche.",
			"24": "<sup>24</sup> Kňazi Šebanja, Jošafát, Netaneel, Amasaj, Zecharja, Benaja, Eliezer boli trubačmi pred Božou archou. Obéd-Edóm a Jechija boli vrátnikmi pri arche.",
			"25": "<sup>25</sup> Dávid, starší Izraela a tisícnici šli nadšene vyniesť archu Hospodinovej zmluvy z Obéd-Edómovho domu.",
			"26": "<sup>26</sup> Pretože Boh pomáhal levitom, ktorí niesli archu Hospodinovej zmluvy, obetovali sedem býčkov a sedem baranov.",
			"27": "<sup>27</sup> Dávid bol odetý do jemného ľanového plášťa i všetci leviti, ktorí niesli archu, speváci a Kenanja, vedúci pri prenášaní. Dávid mal na sebe ľanový efód.",
			"28": "<sup>28</sup> Celý Izrael sprevádzal archu Hospodinovej zmluvy hlučne, za zvuku rohu, trúb a cimbalov, hrou na harfách a citarách.",
			"29": "<sup>29</sup> Keď archa Hospodinovej zmluvy vošla do Dávidovho mesta, Šaulova dcéra Michal sa práve pozerala von oknom. Keď zbadala, ako kráľ Dávid poskakuje a tancuje, pohrdla ním v srdci."
		},
		"16": {
			"1": "<sup>1</sup> Božiu archu preniesli a uložili v stane, ktorý pre ňu roztiahol Dávid. Potom prinášali pred Bohom spaľované obety a obety spoločenstva.",
			"2": "<sup>2</sup> Keď Dávid dokončil spaľovanú obetu a obety spoločenstva, požehnal ľud v Hospodinovom mene.",
			"3": "<sup>3</sup> Potom nadelil všetkým Izraelitom, mužovi i žene, každému bochník chleba, datľový a hrozienkový koláč.",
			"4": "<sup>4</sup> Niektorých levitov určil na službu pred Hospodinovou archou, aby vyznávali, chválili a oslavovali Hospodina, Boha Izraela.",
			"5": "<sup>5</sup> Vedúcim bol Asáf, jeho zástupcom Zecharja; Jeíel, Šemiramót, Jechíel, Mattitja, Eliáb, Benaja, Obéd-Edóm a Jeíel hrali na harfách a citarách, Asáf na cimbale.",
			"6": "<sup>6</sup> Kňazi Benaja a Jachazíel neprestajne hrali na trúbach pred archou Božej zmluvy.",
			"7": "<sup>7</sup> V ten deň Dávid prvý raz poveril Asáfa a jeho bratov, aby vzdávali vďaku Hospodinovi takto:",
			"8": "<sup>8</sup> „Ďakujte Hospodinovi, vzývajte jeho meno! Medzi národmi oznamujte jeho skutky!",
			"9": "<sup>9</sup> Spievajte mu, hrajte mu! Rozprávajte o všetkých jeho divoch!",
			"10": "<sup>10</sup> Chváľte sa jeho svätým menom! Nech sa raduje srdce tých, čo hľadajú Hospodina!",
			"11": "<sup>11</sup> Dopytujte sa na Hospodina a jeho moc, ustavične hľadajte jeho tvár!",
			"12": "<sup>12</sup> Spomínajte na divy, ktoré konal, na znamenia a na rozsudky jeho úst,",
			"13": "<sup>13</sup> vy, potomstvo Izraela, jeho služobníka, synovia Jákoba, jeho vyvolení!",
			"14": "<sup>14</sup> On, Hospodin, je náš Boh, jeho rozhodnutia platia na celej zemi.",
			"15": "<sup>15</sup> Večne pamätajte na jeho zmluvu, na slovo, ktoré prikázal tisícim pokoleniam.",
			"16": "<sup>16</sup> Uzavrel ju s Abrahámom a prísahou potvrdil Izákovi,",
			"17": "<sup>17</sup> ako zákon ju určil Jákobovi Izraelovi ako večnú zmluvu,",
			"18": "<sup>18</sup> keď povedal: ‚Tebe dám krajinu Kanaán do dedičného vlastníctva.‘",
			"19": "<sup>19</sup> Keď vás bolo ešte malý počet, sotva hŕstka, a boli ste v nej len cudzincami,",
			"20": "<sup>20</sup> keď putovali od národa k národu a z jedného kráľovstva k inému ľudu,",
			"21": "<sup>21</sup> nedovolil, aby ich niekto utláčal, pre nich pokarhal aj kráľov:",
			"22": "<sup>22</sup> ‚Nedotýkajte sa mojich pomazaných, neubližujte mojim prorokom!‘",
			"23": "<sup>23</sup> Spievaj Hospodinovi, celá zem, deň čo deň ohlasujte jeho spásu!",
			"24": "<sup>24</sup> Hovorte medzi pohanmi o jeho sláve, o jeho divoch medzi všetkými národmi,",
			"25": "<sup>25</sup> lebo Hospodin je veľký a hoden všetkej chvály, báť sa ho treba viac než všetkých bohov.",
			"26": "<sup>26</sup> Všetci pohanskí bohovia sú len modly, Hospodin však stvoril nebesia.",
			"27": "<sup>27</sup> Predchádza ho velebná dôstojnosť, moc a radosť je tam, kde prebýva.",
			"28": "<sup>28</sup> Rody národov, vzdávajte Hospodinovi, vzdávajte Hospodinovi slávu a moc!",
			"29": "<sup>29</sup> Vzdávajte Hospodinovi slávu hodnú jeho mena! Prineste obetný dar a vstúpte pred neho! Klaňajte sa Hospodinovi v jeho svätej nádhere.",
			"30": "<sup>30</sup> Chvej sa pred ním, celá zem! Svet je založený pevne, nepohne sa!",
			"31": "<sup>31</sup> Nech sa radujú nebesia a plesá zem, nech vravia medzi národmi: ‚Hospodin je kráľom!‘",
			"32": "<sup>32</sup> Nech hučí more i to, čo je v ňom! Nech jasá pole i všetko, čo je na ňom!",
			"33": "<sup>33</sup> Nech plesajú všetky lesné stromy pred Hospodinom, lebo prichádza súdiť zem.",
			"34": "<sup>34</sup> Ďakujte Hospodinovi, lebo je dobrý, lebo naveky trvá jeho milosť.",
			"35": "<sup>35</sup> Volajte: ‚Zachráň nás, Bože, náš záchranca! Spomedzi pohanov nás zhromaždi a vysloboď, aby sme mohli ďakovať tvojmu svätému menu a honosiť sa tvojou chválou!",
			"36": "<sup>36</sup> Zvelebený buď Hospodin, Boh Izraela, od vekov až naveky!‘“ A všetok ľud odpovedal: „Amen“ a „Haleluja.“",
			"37": "<sup>37</sup> Pred archou Hospodinovej zmluvy ponechal Asáfa a jeho bratov, aby tam ustavične konali službu podľa každodenného poriadku,",
			"38": "<sup>38</sup> ďalej Obéd-Edóma a jeho šesťdesiatosem bratov. Jedutúnov syn Obéd-Edóm a Chosa boli vrátnikmi.",
			"39": "<sup>39</sup> Kňaza Cadóka a kňazov, jeho bratov, ponechal pred príbytkom Hospodina na výšine v Gibeóne.",
			"40": "<sup>40</sup> Mali prinášať na oltári na spaľované obety Hospodinovi ustavične ráno i večer spaľované obety, a to presne tak, ako je predpísané v zákone Hospodina, ktorý prikázal Izraelu.",
			"41": "<sup>41</sup> S nimi boli Hemán, Jedutún a ostatní vybraní a vymenovaní, aby vzdávali Hospodinovi vďaku za to, že naveky trvá jeho milosť.",
			"42": "<sup>42</sup> Hemán a Jedutún mali pri sebe trúby a cimbaly a nástroje pre náboženské piesne. Jedutúnovci boli pri bráne.",
			"43": "<sup>43</sup> Potom sa všetok ľud rozišiel domov. Pobral sa i Dávid pozdraviť si rodinu."
		},
		"17": {
			"1": "<sup>1</sup> Keď sa už Dávid usadil vo svojom paláci, povedal prorokovi Nátanovi: „Ja síce bývam v cédrovom paláci, ale archa Hospodinovej zmluvy je len pod stanovými závesmi.“",
			"2": "<sup>2</sup> Nátan odvetil Dávidovi: „Urob všetko, čo máš na mysli, lebo Boh je s tebou.“",
			"3": "<sup>3</sup> Tej istej noci zaznelo slovo Boha Nátanovi:",
			"4": "<sup>4</sup> „Odkáž môjmu služobníkovi Dávidovi: Takto vraví Hospodin: ‚Nie, nepostavíš mi dom na bývanie.",
			"5": "<sup>5</sup> Veď som nebýval v dome odvtedy, ako som vyviedol Izrael, až dodnes, ale som sa sťahoval zo stanu do stanu a z príbytku do príbytku.",
			"6": "<sup>6</sup> Keď som tak putoval s celým Izraelom, či som sa zmienil niektorým sudcom Izraela, ktorým som prikázal viesť môj ľud: Prečo mi nestaviate cédrový dom?‘",
			"7": "<sup>7</sup> Môjmu služobníkovi Dávidovi teda odkáž: Takto vraví Hospodin zástupov: ‚Vzal som ťa od pastvy, od zavracania oviec, aby si sa stal vojvodcom môjho izraelského ľudu.",
			"8": "<sup>8</sup> Sprevádzal som ťa, kam si len šiel a vyhnal som spred teba všetkých tvojich nepriateľov. Urobím ťa slávnym, ako sú pozemskí velikáni.",
			"9": "<sup>9</sup> Určím miesto pre môj izraelský ľud, zasadím ho, takže bude mať svoj domov a nemusí sa už znepokojovať. Ničomníci nebudú s ním už tvrdo zaobchádzať ako predtým,",
			"10": "<sup>10</sup> ako za čias, keď som ustanovil sudcov nad svojím izraelským ľudom. Pokorím všetkých tvojich nepriateľov. Oznamujem ti, že Hospodin postaví dom tebe.",
			"11": "<sup>11</sup> Keď nadíde čas tvojho odchodu k predkom, ustanovím ti nástupcu spomedzi vlastných synov a upevním mu kráľovstvo.",
			"12": "<sup>12</sup> On mi postaví dom a ja mu natrvalo upevním trón.",
			"13": "<sup>13</sup> Budem mu otcom a on mi bude synom. Priazeň mu neodnímem, ako som ju odňal tvojmu predchodcovi,",
			"14": "<sup>14</sup> ale ho ponechám vo svojom dome a vo svojom kráľovstve navždy, jeho trón bude vždy pevný.‘“",
			"15": "<sup>15</sup> Všetko, čo Nátan povedal Dávidovi, bolo v súlade s týmito slovami a celým týmto videním.",
			"16": "<sup>16</sup> Kráľ Dávid si šiel sadnúť pred Hospodina a zvolal: „Ani ja, ani moja rodina sme si nezaslúžili to, že si ma doviedol až sem, Hospodin, Bože.",
			"17": "<sup>17</sup> Ani to ti, Bože, nestačilo, ale si hovoril o budúcnosti domu svojho služobníka a pohliadol si na mňa, Hospodin, Bože, ako na významného človeka.",
			"18": "<sup>18</sup> Čím môže Dávid ešte prispieť k sláve, ktorou si vyznačil svojho služobníka? Ty ho predsa poznáš.",
			"19": "<sup>19</sup> Hospodin, pre svojho služobníka a podľa svojho srdca si uskutočnil celé toto veľké dielo a oznámil si všetky významné udalosti.",
			"20": "<sup>20</sup> Podľa toho, čo sme sami počuli, sa ti, Hospodin, nikto nevyrovná a okrem teba niet Boha.",
			"21": "<sup>21</sup> Kto je ako tvoj ľud Izrael, jediný národ na zemi, ktorý prišiel Boh vykúpiť za svoj ľud a preslávil sa veľkými a úžasnými skutkami, keď si vyhnal národy spred svojho ľudu, ktorý si vykúpil z Egypta?",
			"22": "<sup>22</sup> Svoj izraelský ľud si si navždy privlastnil a sám si sa, Hospodin, stal ich Bohom.",
			"23": "<sup>23</sup> Nuž teda, Hospodin, nech prísľuby, ktoré si vyslovil o svojom služobníkovi a jeho dome, platia navždy. Čo si sľúbil, splň!",
			"24": "<sup>24</sup> Nech tvoje meno nikdy nestráca platnosť a význam, aby sa hovorilo: ‚Hospodin zástupov, Boh Izraela, je pre Izrael Bohom!‘ A dom tvojho služobníka Dávida nech je pevný pred tebou.",
			"25": "<sup>25</sup> Veď ty, Bože môj, si vyjavil svojmu služobníkovi, že mu postavíš dom. Preto sa tvoj služobník osmelil modliť sa pred tebou.",
			"26": "<sup>26</sup> Teraz však, Hospodin, ty sám si Boh, keď si vyslovil o svojom služobníkovi tento priaznivý prísľub,",
			"27": "<sup>27</sup> požehnaj láskavo dom svojho služobníka, aby vždy obstál pred tebou. Veď čo ty, Hospodin, požehnáš, je navždy požehnané.“"
		},
		"18": {
			"1": "<sup>1</sup> Po nejakom čase porazil Dávid Filištíncov, podrobil si ich a odňal im Gat s priľahlými osadami.",
			"2": "<sup>2</sup> Porazil aj Moáb, takže sa Moábčania stali Dávidovými poddanými a poplatníkmi.",
			"3": "<sup>3</sup> Ďalej porazil Dávid Hadad-Ezera, kráľa Coby, pri Chamáte, ktorý si šiel upevniť moc na rieke Eufrat.",
			"4": "<sup>4</sup> Dávid mu odňal tisíc vozov, sedemtisíc jazdcov a dvadsaťtisíc pešiakov. Ochromil kone k bojovým vozom a ponechal len sto záprahov.",
			"5": "<sup>5</sup> Keď prišli Aramejčania z Damasku na pomoc Hadad-Ezerovi, kráľovi Coby, Dávid z nich pobil dvadsaťdvatisíc mužov a",
			"6": "<sup>6</sup> rozmiestnil v damaskom Arame posádky. Tak sa stali Aramejčania Dávidovými poplatníkmi. Hospodin pomáhal Dávidovi, kamkoľvek šiel.",
			"7": "<sup>7</sup> Dávid vzal zlaté štíty, čo používali Hadad-Ezerovi služobníci, a dopravil ich do Jeruzalema.",
			"8": "<sup>8</sup> Z Tibchatu a Kúnu, Hadad-Ezerových miest, nabral Dávid značné množstvo bronzu. Z neho potom vyhotovil Šalamún bronzové more, stĺpy a bronzové predmety.",
			"9": "<sup>9</sup> Keď sa kráľ Chamátu Tóu dopočul, že Dávid porazil celé vojsko Hadad-Ezera, kráľa Coby,",
			"10": "<sup>10</sup> vyslal svojho syna Hadoráma ku kráľovi Dávidovi, aby ho pozdravil a blahoželal mu k víťazstvu v boji proti Hadad-Ezerovi. Bol totiž s Hadad-Ezerom vo vojnovom stave. Poslal mu i rozličné zlaté, strieborné a bronzové predmety.",
			"11": "<sup>11</sup> Tie zasvätil kráľ Dávid Hospodinovi spolu so striebrom a zlatom, ktoré priniesol od rôznych národov: od Edómčanov, Moábčanov, Ammónčanov, Filištíncov a Amalékov.",
			"12": "<sup>12</sup> Abšaj, Cerujin syn, porazil v Soľnom údolí osemnásťtisíc Edómčanov",
			"13": "<sup>13</sup> a po Edómsku rozmiestnil posádky. Tak si Dávid podrobil všetkých Edómčanov. Hospodin pomáhal Dávidovi, kamkoľvek šiel.",
			"14": "<sup>14</sup> Dávid vládol nad celým Izraelom a vysluhoval právo a spravodlivosť všetkému svojmu ľudu.",
			"15": "<sup>15</sup> Joáb, Cerujin syn, velil vojsku, Achilúdov syn Jošafát bol kancelárom,",
			"16": "<sup>16</sup> Achitúbov syn Cadók a Ebjatárov syn Abimelech boli kňazmi, Šavša bol pisárom,",
			"17": "<sup>17</sup> Jojadov syn Benaja velil Kereťanom a Peleťanom a Dávidovi synovia zaujímali popredné postavenie pri kráľovi."
		},
		"19": {
			"1": "<sup>1</sup> Neskôr, keď zomrel ammónsky kráľ Nacháš, stal sa namiesto neho kráľom jeho syn.",
			"2": "<sup>2</sup> Dávid uvažoval: „Preukážem dobrú vôľu Nachášovmu synovi Chanúnovi, pretože i jeho otec sa tak zachoval ku mne.“ Tak vypravil Dávid poslov, aby mu vyslovili sústrasť nad smrťou jeho otca. Dávidovi služobníci prišli k Chanúnovi do krajiny Ammónčanov vysloviť sústrasť.",
			"3": "<sup>3</sup> Ammónski hodnostári však povedali Chanúnovi: „Nazdávaš sa, že si chce Dávid uctiť pamiatku tvojho otca, keď k tebe posiela tešiteľov? Jeho služobníci prišli k tebe skôr preto, aby preskúmali, prepátrali a presliedili krajinu.“",
			"4": "<sup>4</sup> Nato uchopil Chanún Dávidových služobníkov, oholil ich, odrezal im polovicu šiat až po rozkrok a prepustil ich.",
			"5": "<sup>5</sup> Keď odišli, dostal Dávid hlásenie o tých mužoch. Vypravil im naproti poslov, lebo tí muži boli veľmi zhanobení. Kráľ odkázal: „Zostaňte v Jerichu, kým vám nenarastú brady, až potom sa vráťte!“",
			"6": "<sup>6</sup> Keď Ammónčania vybadali, že sa Dávidovi zošklivili, Chanún a Ammónčania poslali tisíc talentov striebra, aby si najali vozy a jazdcov z Mezopotámie, z Aram-Maachy a z Coby.",
			"7": "<sup>7</sup> Najali si tridsaťdvatisíc vozov i kráľa Maachy s jeho ľudom. Tí prišli a utáborili sa pred Medebou, kým Ammónčania sa dohrnuli zo svojich miest a nastúpili do boja.",
			"8": "<sup>8</sup> Keď sa to Dávid dopočul, vyslal Joába s celým vojskom udatných hrdinov.",
			"9": "<sup>9</sup> Ammónčania vyrazili a zoradili sa do boja pri vchode do ich mesta. Králi, čo prišli, stáli osobitne v poli.",
			"10": "<sup>10</sup> Keď Joáb zbadal, že mu hrozí boj spredu i zozadu, vybral si z celého Izraela tých najschopnejších a rozostavil sa proti Aramejčanom do boja.",
			"11": "<sup>11</sup> Ostatok ľudu zveril veleniu svojho brata Abšaja a oni zaujali bojové postavenie proti Ammónčanom.",
			"12": "<sup>12</sup> Povedal: „Ak by boli Aramejčania u mňa v presile, prídeš mi na pomoc. Ak by však u teba boli v presile Ammónčania, pomôžem ja tebe.",
			"13": "<sup>13</sup> Buď udatný a udatne si počínajme za svoj ľud a za mestá svojho Boha. Hospodin nech urobí, čo uzná za dobré.“",
			"14": "<sup>14</sup> Joáb so svojím ľudom pritiahol k Aramejčanom do boja a tí pred ním zutekali.",
			"15": "<sup>15</sup> Keď Ammónčania zbadali, že Aramejčania utekajú, dali sa na útek aj oni pred jeho bratom Abšajom a stiahli sa do mesta. Nato Joáb odišiel do Jeruzalema.",
			"16": "<sup>16</sup> Keď Aramejčania videli, že ich Izraeliti porazili, vypravili poslov, ktorí priviedli Aramejčanov spoza Eufratu. Viedol ich Šofach, veliteľ Hadad-Ezerovho vojska.",
			"17": "<sup>17</sup> Keď o tom dostal Dávid hlásenie, zhromaždil celý Izrael, prebrodil sa cez Jordán, postúpil k nim a prichystal sa proti nim do boja. Dávid sa pripravil na bojové stretnutie s Aramejčanmi, ktorí naňho zaútočili.",
			"18": "<sup>18</sup> No Aramejčania pred Izraelitmi utiekli a Dávid pobil sedemtisíc jazdcov a štyridsaťtisíc pešiakov a usmrtil i veliteľa vojska Šofacha.",
			"19": "<sup>19</sup> Keď Hadad-Ezerovi služobníci videli, že ich Izrael porazil, uzavreli s Dávidom mier a podriadili sa mu. Aramejčania už nikdy nechceli ísť na pomoc Ammónčanom."
		},
		"20": {
			"1": "<sup>1</sup> Nasledujúceho roku, v čase kráľovských výprav, viedol Joáb ozbrojené sily a spustošil krajinu Ammónčanov. Potom prišiel k Rabbe a dobyl ju. Kým sa Dávid zdržiaval v Jeruzaleme, Joáb ju po dobytí zrúcal.",
			"2": "<sup>2</sup> Dávid sňal z hlavy ich kráľa korunu, ktorej zlato vážilo asi talent a bol v nej drahokam. Bola potom na Dávidovej hlave. Z mesta odvliekol úžasnú korisť.",
			"3": "<sup>3</sup> Ľud, ktorý tam bol, vyviedol von a pridelil k pílam, železným čakanom a k sekerám. Tak naložil Dávid so všetkými ammónskymi mestami. Potom sa so všetkým ľudom vrátil do Jeruzalema.",
			"4": "<sup>4</sup> Neskôr došlo pri Gezeri k boju proti Filištíncom. Vtedy Sibbechaj z Chuše zabil Safa, potomka Refajov, a tak boli Filištínci pokorení.",
			"5": "<sup>5</sup> V ďalšom boji proti Filištíncom zabil Jaírov syn Elchanán Lachmiho, brata Goliáša z Gatu, ktorého drevená časť kopije bola ako tkáčsky návoj.",
			"6": "<sup>6</sup> V inom boji, ktorý sa viedol pri Gate, bol aj ozrutný muž, čo mal po šesť, dovedna dvadsaťštyri prstov. I ten pochádzal z obrov.",
			"7": "<sup>7</sup> Keď však hanobil Izrael, zabil ho Jonatán, syn Šimeu, Dávidovho brata.",
			"8": "<sup>8</sup> Tí pochádzali z obrov v Gate. Padli rukou Dávida a jeho služobníkov."
		},
		"21": {
			"1": "<sup>1</sup> Proti Izraelu povstal satan a naviedol Dávida, aby sčítal Izrael.",
			"2": "<sup>2</sup> Dávid prikázal Joábovi a hodnostárom ľudu: „Choďte a spočítajte Izrael od Beér-Šeby po Dán. Prineste mi výsledok, aby som vedel, koľko ho je.“",
			"3": "<sup>3</sup> Joáb však namietal: „Nech Hospodin stonásobne rozmnoží svoj ľud! Beztak sú všetci, môj pán a kráľ, sluhami môjho pána. Prečo si to môj pán žiada? Prečo by sa tým mal Izrael previniť?“",
			"4": "<sup>4</sup> Kráľov príkaz však Joába prinútil. Tak sa Joáb vydal na cestu, prešiel celý Izrael a prišiel do Jeruzalema.",
			"5": "<sup>5</sup> Joáb oznámil Dávidovi výsledok sčítania ľudu: celý Izrael vykazoval milión stotisíc mužov, ktorí narábali mečom, a Júdovcov bolo štyristosedemdesiattisíc mužov, schopných narábať mečom.",
			"6": "<sup>6</sup> Léviho a Benjamína medzi nich nezarátal, lebo kráľov príkaz sa Joábovi sprotivil.",
			"7": "<sup>7</sup> Ani Bohu sa tá vec nepáčila, a preto Izrael potrestal.",
			"8": "<sup>8</sup> Vtedy Dávid Bohu povedal: „Veľmi som zhrešil, keď som vykonal sčítanie. Teraz, prosím, odpusť svojmu služobníkovi vinu, lebo som konal veľmi nerozvážne.“",
			"9": "<sup>9</sup> Hospodin povedal Dávidovmu vidcovi Gádovi:",
			"10": "<sup>10</sup> „Odkáž Dávidovi: Takto vraví Hospodin: ‚Dávam ti na výber tri možnosti, jednu z nich si zvoľ a ja ti ju splním.‘“",
			"11": "<sup>11</sup> Nato odišiel Gád k Dávidovi a povedal mu: „Takto vraví Hospodin: ‚Vyber si:",
			"12": "<sup>12</sup> buď tri roky hladu, alebo tri mesiace porážok od tvojich nepriateľov, takže by ťa stíhal nepriateľov meč, alebo tri dni Hospodinovho meča, takže by v krajine nastal mor a anjel Hospodina by pustošil na celom území Izraela.‘ Premysli si teda, čo mám odkázať tomu, ktorý ma poslal.“",
			"13": "<sup>13</sup> Dávid povedal Gádovi: „Je mi veľmi úzko. Nech sa radšej dostanem do Hospodinovej ruky, lebo je nesmierne jeho milosrdenstvo, len nech sa nedostanem do ruky človeka.“",
			"14": "<sup>14</sup> Hospodin dopustil na Izrael mor, takže naň zahynulo sedemdesiattisíc Izraelitov.",
			"15": "<sup>15</sup> Boh poslal na pustošenie Jeruzalema anjela. Keď anjel pustošil, Hospodin sa pozrel a oľutoval pohromu. Pustošiacemu anjelovi prikázal: „To stačí! Prestaň!“ Hospodinov anjel stál pri humne Jebúseja Ornána.",
			"16": "<sup>16</sup> Keď sa Dávid rozhliadol, uvidel medzi zemou a nebom stáť Hospodinovho anjela s vytaseným mečom v ruke, zamiereným na Jeruzalem. Vtedy padol Dávid i starší, odiati vrecovinou, na tvár.",
			"17": "<sup>17</sup> Dávid povedal Bohu: „Ja som nariadil sčítať ľud a dopustil sa zlého. Čo však urobilo toto stádo? Hospodin, Bože môj, treba potrestať mňa a moju rodinu, nie však tvoj ľud.“",
			"18": "<sup>18</sup> Vtedy odkázal Hospodinov anjel po Gádovi Dávidovi, aby šiel postaviť na humne Jebúseja Ornána Hospodinovi oltár.",
			"19": "<sup>19</sup> Dávid odišiel na výzvu Gáda, ktorý hovoril v mene Hospodina.",
			"20": "<sup>20</sup> Keď sa Ornán obrátil, uzrel anjela. Jeho štyria synovia, ktorí boli s ním, sa skryli. Ornán práve mlátil pšenicu.",
			"21": "<sup>21</sup> Dávid sa pobral k nemu. Keď sa Ornán obzrel a uvidel Dávida, vyšiel z humna a poklonil sa mu tvárou po zem.",
			"22": "<sup>22</sup> Dávid navrhol Ornánovi: „Prepusť mi pozemok, na ktorom je humno, aby som na ňom postavil Hospodinovi oltár. Tak sa zastaví na ľude pohroma. Predaj mi ho za plnú cenu.“",
			"23": "<sup>23</sup> Ornán odpovedal Dávidovi: „Vezmi si ho. Nech môj pán, kráľ, urobí, čo uzná za vhodné. Pozri, dávam dobytok na spaľované obety, náradie na mlátenie ako palivo i pšenicu na pokrmovú obetu; všetko dávam.“",
			"24": "<sup>24</sup> Kráľ Dávid však povedal Ornánovi: „To nie! Odkúpim to len za plnú cenu. Neprinesiem predsa Hospodinovi to, čo je tvoje, ani nebudem obetovať spaľovanú obetu, ktorá ma nič nestojí.“",
			"25": "<sup>25</sup> Dávid odvážil Ornánovi za ten pozemok šesťsto šeklov zlata.",
			"26": "<sup>26</sup> Dávid tam postavil Hospodinovi oltár, na ktorom prinášal spaľované obety a obety spoločenstva. Keď vzýval Hospodina, odpovedal mu zoslaním ohňa z neba na oltár na spaľované obety.",
			"27": "<sup>27</sup> Na príkaz Hospodina si anjel zastrčil meč do pošvy.",
			"28": "<sup>28</sup> Odvtedy, ako Dávid videl, že mu Hospodin na humne Jebúseja Ornána odpovedal, obetoval tam.",
			"29": "<sup>29</sup> Príbytok Hospodina, ktorý zhotovil Mojžiš na púšti, a oltár na spaľované obety sa vtedy nachádzali na výšine v Gibeóne.",
			"30": "<sup>30</sup> Dávid však nemohol k nemu zájsť, aby sa poradil s Bohom, lebo ho vydesil meč Hospodinovho anjela."
		},
		"22": {
			"1": "<sup>1</sup> Dávid vyhlásil: „Toto bude dom Hospodina, Boha, a tu bude mať Izrael oltár na spaľované obety.“",
			"2": "<sup>2</sup> Dávid dal sústrediť cudzincov, ktorí boli v Izraeli, a určil kamenárov na okresávanie kamenných kvádrov na stavbu Božieho domu.",
			"3": "<sup>3</sup> Dávid zaobstaral množstvo železa na klince do krídel brán i na skoby, množstvo bronzu, ktoré sa nedalo odvážiť,",
			"4": "<sup>4</sup> a nesmierne veľa cédrového dreva. Sidončania a Týrčania totiž dodávali Dávidovi mnoho cédrového dreva.",
			"5": "<sup>5</sup> Dávid povedal: „Môj syn Šalamún je ešte neskúsený mladík, dom však, ktorý sa má postaviť Hospodinovi, treba vybudovať tak, aby vzbudzoval obdiv a nádheru vo všetkých krajinách. Musím na to urobiť prípravy.“ Dávid zaobstaral ešte pred svojou smrťou množstvo materiálu.",
			"6": "<sup>6</sup> Potom si zavolal syna Šalamúna a prikázal mu postaviť dom Hospodinovi, Bohu Izraela.",
			"7": "<sup>7</sup> Dávid hovoril svojmu synovi Šalamúnovi: „Sám som pomýšľal postaviť dom menu Hospodina, svojho Boha.",
			"8": "<sup>8</sup> Hospodin mi však vyčítal: ‚Prelial si veľa krvi a zviedol urputné boje. Nepostavíš dom môjmu menu, lebo si predo mnou prelial veľa krvi na zem.",
			"9": "<sup>9</sup> Narodí sa ti však syn, ktorý prinesie pokoj. Zaistím mu mier od všetkých jeho okolitých nepriateľov. Veď sa bude volať Šalamún. Za jeho vlády dám Izraelu mier a spokojnosť.",
			"10": "<sup>10</sup> On postaví môjmu menu dom. Bude mi synom a ja jemu otcom. Jeho kráľovský trón nad Izraelom natrvalo upevním.‘",
			"11": "<sup>11</sup> Teraz, syn môj, nech ťa sprevádza Hospodin, aby si úspešne vybudoval dom Hospodina, svojho Boha, ako o tebe povedal.",
			"12": "<sup>12</sup> Len nech ti dá Hospodin múdrosť a um. Nech ťa ustanoví nad Izraelom, aby si zachovával zákon Hospodina, svojho Boha.",
			"13": "<sup>13</sup> Úspech dosiahneš vtedy, keď budeš dbať na plnenie ustanovení a právnych predpisov, ktoré vydal Hospodin Mojžišovi pre Izrael. Buď silný a odvážny! Neboj sa a nestrachuj!",
			"14": "<sup>14</sup> Napriek svojim trápeniam som zaobstaral pre Hospodinov dom stotisíc talentov zlata, milión talentov striebra a množstvo bronzu a železa, ktoré sa nedá zvážiť, takže je toho hojnosť. Zadovážil som i drevo a kamene. K tomu musíš ešte pridať.",
			"15": "<sup>15</sup> Máš pri sebe mnohých remeselníkov: kamenárov, tesárov a rôznych majstrov na každú prácu.",
			"16": "<sup>16</sup> Zlata, striebra, bronzu a železa je nespočetné množstvo. Daj sa do práce a Hospodin nech je s tebou!“",
			"17": "<sup>17</sup> Nato vyzval Dávid všetkých izraelských hodnostárov, aby boli jeho synovi Šalamúnovi na pomoci:",
			"18": "<sup>18</sup> „Hospodin, váš Boh, je s vami a zaistil vám mier od okolia, lebo mi vydal do moci obyvateľov krajiny, ktorá musí poslúchať Hospodina a jeho ľud.",
			"19": "<sup>19</sup> Teraz sa srdcom a celou bytosťou zamerajte na hľadanie Hospodina, svojho Boha. Dajte sa do stavby svätyne Hospodina, Boha, aby sa preniesla archa Hospodinovej zmluvy i posvätné náboženské predmety do domu, ktorý sa má postaviť na počesť Hospodinovho mena.“"
		},
		"23": {
			"1": "<sup>1</sup> Keď Dávid zostarol a mnoho prežil, ustanovil svojho syna Šalamúna za kráľa nad Izraelom.",
			"2": "<sup>2</sup> Zhromaždil všetkých izraelských hodnostárov, kňazov a levitov.",
			"3": "<sup>3</sup> Spočítali levitov od tridsaťročných nahor. Ich počet bol tridsaťosemtisíc mužov.",
			"4": "<sup>4</sup> Dávid povedal: „Z nich som poveril riadením služby Hospodinovho domu dvadsaťštyritisíc, úradníkov a sudcov šesťtisíc,",
			"5": "<sup>5</sup> vrátnikov štyritisíc a spevákov štyritisíc, tí mali chváliť Hospodina a hrať na nástrojoch, ktoré som zhotovil.“",
			"6": "<sup>6</sup> Dávid ich zadelil do tried podľa Léviho synov: Geršóna, Koháta a Merariho.",
			"7": "<sup>7</sup> Ku Geršónovcom patrili: Ladán a Šimei.",
			"8": "<sup>8</sup> Ladánovi synovia: predák Jechíel, Zetám a Joel, traja.",
			"9": "<sup>9</sup> Šimeiho synovia: Šelomót, Chazíel a Harán, traja. Tí boli predákmi Ladánových rodín.",
			"10": "<sup>10</sup> Šimeiho synovia: Jachat, Zina, Jeúš a Bería. To boli štyria Šimeiho synovia.",
			"11": "<sup>11</sup> Jachat bol predákom, Ziza druhým, Jeúš a Bería neoplývali synmi, takže tvorili jednu rodinu, jednu triedu.",
			"12": "<sup>12</sup> Kohátovi synovia: Amrám, Jishár, Chebrón a Uzzíel, štyria.",
			"13": "<sup>13</sup> Amrámovi synovia: Áron a Mojžiš. Áron mal i so svojimi synmi za úlohu udržiavať neprestajne v posvätnom stave veľsvätyňu, páliť tymian pred Hospodinom, posluhovať mu a v jeho mene stále požehnávať.",
			"14": "<sup>14</sup> Synovia Božieho muža Mojžiša boli pripočítaní ku kmeňu Lévi.",
			"15": "<sup>15</sup> Mojžišovi synovia boli Geršóm a Eliezer.",
			"16": "<sup>16</sup> Synom Geršóma bol predák Šebuel.",
			"17": "<sup>17</sup> Synom Eliezera bol predák Rechabja. Iných synov Eliezer nemal, zato Rechabja ich mal veľa.",
			"18": "<sup>18</sup> Jishárov syn: predák Šelomót.",
			"19": "<sup>19</sup> Chebrónovi synovia: predák Jerija, druhý Amarja, tretí Jachazíel, štvrtý Jekamám.",
			"20": "<sup>20</sup> Uzzíelovi synovia: predák Micha, druhý Jiššija.",
			"21": "<sup>21</sup> Merariho synovia: Machli a Muši. Machliho synovia: Eleazár a Kíš.",
			"22": "<sup>22</sup> Eleazár zomrel bez synov, mal iba dcéry. Tie si pobrali Kíšovci, ich príbuzní.",
			"23": "<sup>23</sup> Mušiho synovia: Machli, Éder, Jeremót, traja.",
			"24": "<sup>24</sup> To boli Léviho synovia podľa jednotlivých rodín, rodinní predáci, ako boli zaradení v mennom zozname ich príslušníkov, zamestnaných v službe Hospodinovho domu od dvadsaťročných nahor.",
			"25": "<sup>25</sup> Dávid totiž povedal: „Hospodin, Boh Izraela, dožičil svojmu ľudu mier a natrvalo sa usídlil v Jeruzaleme.",
			"26": "<sup>26</sup> Leviti už nemusia nosiť príbytok a rôzne predmety k jeho službe.“",
			"27": "<sup>27</sup> Podľa posledných Dávidových úprav sa totiž započítavali Léviho synovia od dvadsiatich rokov a viac.",
			"28": "<sup>28</sup> „Ich povinnosťou bude pomáhať Áronovcom v službe Hospodinovho domu, dozerať na nádvoria, na komory, udržiavať čistotu všetkého svätého, dohliadať na bohoslužobné úkony v chráme,",
			"29": "<sup>29</sup> ďalej starať sa o predkladané chleby, o jemnú múku, o pokrmovú obetu, o nekvasené posúchy, o panvicu, o miesenie cesta a o rôzne duté a dĺžkové miery.",
			"30": "<sup>30</sup> Každé ráno majú nastúpiť na oslavu a chválu Hospodina, podobne večer,",
			"31": "<sup>31</sup> na každé prinášanie spaľovaných obiet pre Hospodina, a to cez soboty, na novmesiace a na sviatky v takom počte, ako to požaduje predpis o obetách. Majú ustavične slúžiť pred Hospodinom,",
			"32": "<sup>32</sup> starať sa o službu v stane stretávania, ako i vo svätyni, a obsluhovať svojich bratov Áronovcov pri službe v Hospodinovom dome.“"
		},
		"24": {
			"1": "<sup>1</sup> Áronovi potomkovia mali svoje triedy. Áronovi synovia boli: Nadáb, Abihu, Eleazár a Itamár.",
			"2": "<sup>2</sup> Nadáb a Abihu zomreli prv než ich otec. Keďže nemali synov, kňazskú službu prevzali: Eleazár a Itamár.",
			"3": "<sup>3</sup> Spolu s Cadókom, Eleazárovým potomkom, a Achimelechom, Itamárovým potomkom, im Dávid rozdelil služobné úlohy.",
			"4": "<sup>4</sup> U Eleazárovcov sa našlo viac vedúcich osôb než u Itamárovcov. Vyriešili to tak, že Eleazárovci dostali šestnásť a Itamárovci osem rodinných predákov.",
			"5": "<sup>5</sup> Týchto i tamtých podelili lósmi, pretože hodnostármi svätyne i hodnostármi Božími mali byť niektorí Eleazárovci a Itamárovci.",
			"6": "<sup>6</sup> Pisár Šemaja, syn Netaneela, z Léviho kmeňa, ich zapísal v prítomnosti kráľa a hodnostárov: kňaza Cadóka, Achimelecha, Ebjatárovho syna, i predákov kňazských a levitských rodín. Raz bola vylosovaná rodina z Eleazára, raz zase jedna z Itamára.",
			"7": "<sup>7</sup> Prvý lós padol na Jojaríba, druhý na Jedaju,",
			"8": "<sup>8</sup> tretí na Charíma, štvrtý na Seoríma,",
			"9": "<sup>9</sup> piaty na Malkiju, šiesty na Mijjamína,",
			"10": "<sup>10</sup> siedmy na Kóca, ôsmy na Abiju,",
			"11": "<sup>11</sup> deviaty na Ješúu, desiaty na Šechanju,",
			"12": "<sup>12</sup> jedenásty na Eljašíba, dvanásty na Jakíma,",
			"13": "<sup>13</sup> trinásty na Chuppu, štrnásty na Ješebába,",
			"14": "<sup>14</sup> pätnásty na Bilgu, šestnásty na Imméra,",
			"15": "<sup>15</sup> sedemnásty na Chezíra, osemnásty na Happiccéca,",
			"16": "<sup>16</sup> devätnásty na Petachju, dvadsiaty na Jechezkela,",
			"17": "<sup>17</sup> dvadsiaty prvý na Jachína, dvadsiaty druhý na Gamúla,",
			"18": "<sup>18</sup> dvadsiaty tretí na Delaju, dvadsiaty štvrtý na Maazju.",
			"19": "<sup>19</sup> To je ich služobné zaradenie na nástup do Hospodinovho domu podľa poriadku zavedeného ich praotcom Áronom podľa príkazu Hospodina, Boha Izraela.",
			"20": "<sup>20</sup> Ostatní leviti: Z Amrámových synov: Šubael, zo synov Šubaela: Jechdeja.",
			"21": "<sup>21</sup> Pokiaľ ide o Rechabju, z jeho synov bol predákom Jiššija.",
			"22": "<sup>22</sup> Z Jishárovcov: Šelomót: zo synov Šelomóta: Jachat.",
			"23": "<sup>23</sup> Hebronovi synovia: Jerija prvý, Amarja druhý, Jachazíel tretí, Jekamám štvrtý.",
			"24": "<sup>24</sup> Uzzíelov syn: Micha, zo synov Michu: Šamír.",
			"25": "<sup>25</sup> Bratom Michu bol Jiššija. Zo synov Jiššiju: Zecharja.",
			"26": "<sup>26</sup> Merariho synovia: Machli a Muši a synovia Jaaziju, jeho syna.",
			"27": "<sup>27</sup> Merariho synovia po synovi Jaazijovi: Šoham, Zakkúr a Ibri.",
			"28": "<sup>28</sup> Z Machliho: Eleazár, ktorý nemal synov.",
			"29": "<sup>29</sup> Pokiaľ ide o Kíša, jeho synom bol Jerachmeel.",
			"30": "<sup>30</sup> Mušiho synovia: Machli, Éder a Jerimót. To sú leviti podľa svojich rodín.",
			"31": "<sup>31</sup> Aj oni losovali spolu so svojimi bratmi Áronovcami v prítomnosti kráľa Dávida, Cadóka, Achimelecha a predákov kňazských a levitských rodín, a to popredné rodiny rovnako, ako aj ich menej významní bratia."
		},
		"25": {
			"1": "<sup>1</sup> Dávid s vedúcimi služby vyčlenil na zvláštnu úlohu niektorých Asáfovcov, Hemánovcov a Jedutúnovcov, ktorí za sprievodu citár, hárf a cimbalov prorokovali. Zoznam mužov, vykonávajúcich túto službu:",
			"2": "<sup>2</sup> z Asáfovcov: Zakkúr, Jozef, Netanja, Asarela. Asáfovi synovia boli podriadení Asáfovi, ktorý na pokyn kráľa prorokoval.",
			"3": "<sup>3</sup> Z Jedutúna: jeho synovia boli: Gedalja, Ceri, Ješaja, Chašabja a Mattitja, šiesti. Podliehali svojmu otcovi Jedutúnovi, ktorý prorokoval za sprievodu citary na chválu a oslavu Hospodina.",
			"4": "<sup>4</sup> Z Hemána: jeho synovia boli: Bukkija, Mattanja, Uzzíel, Šebuel, Jerimót, Chananja, Chanani, Elíata, Giddalti, Romamti-Ezer, Jošbekaša, Mallóti, Hotír, Machaziót.",
			"5": "<sup>5</sup> Tí všetci boli synovia kráľovho vidca Hemána, aby podľa Božieho slova zvelebovali jeho moc. Boh dal Hemánovi štrnásť synov a tri dcéry.",
			"6": "<sup>6</sup> Všetci títo vystupovali pod vedením svojho otca pri speve v Hospodinovom dome za sprievodu cimbalov, hárf a citár, pri chrámovej bohoslužbe z poverenia kráľa. Toľko o Asáfovi, Jedutúnovi a Hemánovi.",
			"7": "<sup>7</sup> Ich počet spolu s ich bratmi, školenými spevákmi na počesť Hospodina, vykazoval dvestoosemdesiatosem.",
			"8": "<sup>8</sup> O poradie služby losovali, a to začiatočník i skúsený, majster i žiak.",
			"9": "<sup>9</sup> Prvý lós vyšiel na Asáfovca Jozefa, na neho, na jeho synov a bratov, na dvanástich. Druhý na Gedalju, na neho, na jeho bratov a synov, na dvanástich.",
			"10": "<sup>10</sup> Tretí na Zakkúra, na jeho synov a bratov, na dvanástich.",
			"11": "<sup>11</sup> Štvrtý na Jicriho, na jeho synov a bratov, na dvanástich.",
			"12": "<sup>12</sup> Piaty na Netanju, na jeho synov a bratov, na dvanástich.",
			"13": "<sup>13</sup> Šiesty na Bukkiju, na jeho synov a bratov, na dvanástich.",
			"14": "<sup>14</sup> Siedmy na Jesarelu, na jeho synov a bratov, na dvanástich.",
			"15": "<sup>15</sup> Ôsmy na Ješaju, na jeho synov a bratov, na dvanástich.",
			"16": "<sup>16</sup> Deviaty na Mattanju, na jeho synov a bratov, na dvanástich.",
			"17": "<sup>17</sup> Desiaty na Šimeiho, na jeho synov a bratov, na dvanástich.",
			"18": "<sup>18</sup> Jedenásty na Azarela, na jeho synov a bratov, na dvanástich.",
			"19": "<sup>19</sup> Dvanásty na Chašabju, na jeho synov a bratov, na dvanástich.",
			"20": "<sup>20</sup> Trinásty na Šubaela, na jeho synov a bratov, na dvanástich.",
			"21": "<sup>21</sup> Štrnásty na Mattitju, na jeho synov a bratov, na dvanástich.",
			"22": "<sup>22</sup> Pätnásty na Jeremóta, na jeho synov a bratov, na dvanástich.",
			"23": "<sup>23</sup> Šestnásty na Chananju, na jeho synov a bratov, na dvanástich.",
			"24": "<sup>24</sup> Sedemnásty na Jošbekašu, na jeho synov a bratov, na dvanástich.",
			"25": "<sup>25</sup> Osemnásty na Chananiho, na jeho synov a bratov, na dvanástich.",
			"26": "<sup>26</sup> Devätnásty na Mallótiho, na jeho synov a bratov, na dvanástich.",
			"27": "<sup>27</sup> Dvadsiaty na Elíatu, na jeho synov a bratov, na dvanástich.",
			"28": "<sup>28</sup> Dvadsiaty prvý na Hotíra, na jeho synov a bratov, na dvanástich.",
			"29": "<sup>29</sup> Dvadsiaty druhý na Giddaltiho, na jeho synov a bratov, na dvanástich.",
			"30": "<sup>30</sup> Dvadsiaty tretí na Machazióta, na jeho synov a bratov, na dvanástich.",
			"31": "<sup>31</sup> Dvadsiaty štvrtý na Romamti-Ezera, na jeho synov a bratov, na dvanástich."
		},
		"26": {
			"1": "<sup>1</sup> K oddielom vrátnikov patrili: z Korachovcov: Asáfovec Mešelemja, Koreho syn.",
			"2": "<sup>2</sup> Synovia Mešelemju boli: prvorodený Zecharja, druhý Jedíael, tretí Zebadja, štvrtý Jatníel,",
			"3": "<sup>3</sup> piaty Elám, šiesty Jochanán, siedmy Eljehoenaj.",
			"4": "<sup>4</sup> Synovia Obéd-Edóma: prvorodený Šemaja, druhý Jozabád, tretí Joách, štvrtý Sachár, piaty Netaneel,",
			"5": "<sup>5</sup> šiesty Ammíel, siedmy Jissachár, ôsmy Peulletaj; Boh ho totiž požehnal.",
			"6": "<sup>6</sup> Jeho synovi Šemajovi sa narodili synovia, ktorí zaujali vedúce postavenie v rodinách, lebo to boli udatní hrdinovia.",
			"7": "<sup>7</sup> Šemajovi synovia: Otni, Refael, Obéd, Elzabád a jeho bratia Elihu a Semachja, boli to udatní muži.",
			"8": "<sup>8</sup> Tí všetci pochádzali z Obéd-Edómčanov. Oni sami aj ich synovia a bratia boli udatní muži, schopní služby. Bolo ich šesťdesiatdva Obéd-Edómčanov.",
			"9": "<sup>9</sup> Mešelemja mal osemnásť udatných synov a bratov.",
			"10": "<sup>10</sup> Merariovec Chosa mal syna Šimriho, predáka, hoci nebol prvorodený, otec ho dosadil za predáka,",
			"11": "<sup>11</sup> Chilkiju druhého, Tebalju tretieho, Zecharju štvrtého. Všetkých Chosových synov a bratov bolo trinásť.",
			"12": "<sup>12</sup> Tieto oddiely vrátnikov, predáci mužstva, ako aj ich bratia, mali povinnosť konať strážnu službu v Hospodinovom dome.",
			"13": "<sup>13</sup> O každú bránu sa losovalo po rodinách, či bola málo, alebo veľmi početná.",
			"14": "<sup>14</sup> Lós o východnú bránu pripadol Šelemjovi. Keď losovali, Zecharjovi, jeho synovi a múdremu poradcovi, pripadla lósom severná brána,",
			"15": "<sup>15</sup> Obéd-Edómovi južná brána a jeho synom skladisko,",
			"16": "<sup>16</sup> Šuppímovi a Chosovi západná brána Šallechet, odkiaľ vystupuje cesta nahor. Stráž stála vedľa stráže.",
			"17": "<sup>17</sup> Na východnej strane bolo šesť levitov, na severnej strane denne štyria, na južnej denne štyria, pri skladisku dvaja,",
			"18": "<sup>18</sup> pri Parbare na západe: štyria na ceste a dvaja pri Parbare.",
			"19": "<sup>19</sup> To boli oddiely vrátnikov, zostavené z Korachovcov a Merariovcov.",
			"20": "<sup>20</sup> Ich bratia leviti spravovali zásoby Božieho domu a zasvätené poklady.",
			"21": "<sup>21</sup> Potomkovia Geršónovca Ladána, predáci Ladánových rodín, sú Jechíelovci.",
			"22": "<sup>22</sup> Synovia Jechíelovcov Zetám a jeho brat Joel spravovali poklady Hospodinovho domu.",
			"23": "<sup>23</sup> Amrámovcom, Jishárovcom, Chebrónovcom a Uzzíelovcom",
			"24": "<sup>24</sup> bol predstavený nad pokladmi Šebuel, Geršómov syn, Mojžišov vnuk.",
			"25": "<sup>25</sup> Jeho brat Eliezer mal synov: Rechabju, Ješaju, Joráma, Zichriho a Šelomóta.",
			"26": "<sup>26</sup> Ten Šelomót a jeho bratia spravovali všetky zasvätené poklady, ktoré zasvätili kráľ Dávid, predáci rodín, tisícnici, stotníci a iní velitelia vojska.",
			"27": "<sup>27</sup> Čo pochádzalo z vojnovej koristi, to zasvätili pre údržbu Hospodinovho domu.",
			"28": "<sup>28</sup> Všetko, čo zasvätil videc Samuel, Kíšov syn Šaul, Nérov syn Abnér a Cerujin syn Joáb, každý zasvätený dar spravoval Šelomót s bratmi.",
			"29": "<sup>29</sup> Jishárovci, Konanja a jeho synovia, boli určení na mimochrámovú činnosť v Izraeli, a to ako úradníci a sudcovia.",
			"30": "<sup>30</sup> Chebrónovci, Chašabja a jeho bratia, tisícsedemsto udatných mužov, zodpovedali za správu Izraela na západ od Jordánu, za všetky záležitosti Hospodina a za službu kráľovi.",
			"31": "<sup>31</sup> Chebrónovci: predákom ich rodov a rodín bol Jerija. V štyridsiatom roku Dávidovej vlády pri ich previerke sa zistilo, že sú medzi nimi v gileádskom Jazére udatní hrdinovia.",
			"32": "<sup>32</sup> Mal bratov, dvetisícsedemsto udatných rodinných predákov. Tých ustanovil kráľ Dávid nad Rúbenovcami, Gádovcami a polovicou kmeňa Menaššeovcov vo všetkých veciach Božích a kráľovských."
		},
		"27": {
			"1": "<sup>1</sup> Izraeliti podľa svojho počtu, rodinní predáci, tisícnici, stotníci a úradníci, ktorí slúžili kráľovi v rôznych záležitostiach oddielov, ktoré sa striedali každý mesiac, po všetky mesiace roka. Každý taký oddiel mal dvadsaťštyritisíc mužov.",
			"2": "<sup>2</sup> Prvému oddielu v prvom mesiaci velil Jašobám, Zabdíelov syn. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"3": "<sup>3</sup> Patril k Perecovcom a bol hlavným veliteľom všetkých vojsk v prvom mesiaci.",
			"4": "<sup>4</sup> Nad oddielom druhého mesiaca bol Achóachovec Dodaj, ale vojvodcom oddielu bol Miklót. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"5": "<sup>5</sup> Veliteľom tretieho oddielu v treťom mesiaci bol Benaja, syn hlavného kňaza Jojadu. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"6": "<sup>6</sup> Tento Benaja patril ku skupine tridsiatich hrdinov a stal sa zároveň ich veliteľom. Oddielu však velil jeho syn Ammizabád.",
			"7": "<sup>7</sup> Štvrtým veliteľom na štvrtý mesiac bol Asael, Joábov brat, a po ňom jeho syn Zebadja. Jeho oddiel mal dvadsaťštyritisíc mužov.",
			"8": "<sup>8</sup> Piatym veliteľom na piaty mesiac bol Jizrachovec Šamhút. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"9": "<sup>9</sup> Šiestym na šiesty mesiac bol Ira, syn Tekóančana Ikkéša. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"10": "<sup>10</sup> Siedmym na siedmy mesiac bol Pelóňan Chelec z Efrajimovcov. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"11": "<sup>11</sup> Ôsmym na ôsmy mesiac bol Zerachovec Sibbechaj z Chuše. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"12": "<sup>12</sup> Deviatym na deviaty mesiac bol Anatóťan Abíezer z Benjamínovcov. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"13": "<sup>13</sup> Desiatym na desiaty mesiac bol Zerachovec Mahraj z Netofy. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"14": "<sup>14</sup> Jedenástym na jedenásty mesiac bol Piratónčan Benaja z Efrajimovcov. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"15": "<sup>15</sup> Dvanástym na dvanásty mesiac bol Netofan Cheldaj z Otníela. Na jeho oddiel pripadalo dvadsaťštyritisíc mužov.",
			"16": "<sup>16</sup> Velenie izraelských kmeňov: Rúbenovcom velil knieža Eliezer, Zichriho syn, Šimeónovcom Šefatja, syn Maachu,",
			"17": "<sup>17</sup> Léviovcom Chašabja, Kemuelov syn, Áronovcom Cadók,",
			"18": "<sup>18</sup> Júdovcom Elihu z Dávidových bratov, Jissachárovcom Omri, Michaelov syn,",
			"19": "<sup>19</sup> Zebulúnovcom Jišmaja, Obadjov syn, Naftaliovcom Jerimót, Azríelov syn,",
			"20": "<sup>20</sup> Efrajimovcom Hošea, Azazjov syn, západnej časti kmeňa Menašše Joel, Pedajov syn,",
			"21": "<sup>21</sup> gileádskej časti Menaššeho Jiddo, Zecharjov syn, Benjamínovcom Jaasíel, Abnérov syn,",
			"22": "<sup>22</sup> Dánovcom Azarel, Jerochámov syn. To boli náčelníci izraelských kmeňov.",
			"23": "<sup>23</sup> Dávid neuviedol do zoznamu dvadsaťročné a mladšie osoby, lebo Hospodin sľúbil, že rozmnoží Izrael a bude ho ako hviezd na nebi.",
			"24": "<sup>24</sup> Joáb, Cerujin syn, aj začal so sčítaním, ale ho nedokončil, lebo Izrael preň zastihol Boží hnev. Tak sa jeho výsledok ani nedostal do Kroniky kráľa Dávida.",
			"25": "<sup>25</sup> Kráľovské poklady spravoval Azmávet, Adíelov syn, zásoby na poli, v mestách, na vidieku a vo vežiach Jonatán, Uzzijov syn.",
			"26": "<sup>26</sup> Poľnohospodárskych robotníkov pri obrábaní pôdy riadil Ezri, Kelúbov syn.",
			"27": "<sup>27</sup> Vinice spravoval Šimei z Rámy a zásoby vína vo viniciach Šefámčan Zabdi.",
			"28": "<sup>28</sup> Za olivy a divé figovníky zodpovedal na Nížine Gederčan Baal-Chanán, za zásoby oleja Joáš,",
			"29": "<sup>29</sup> za hovädzí dobytok, ktorý sa pásol na Šaróne, Šarónčan Šitraj, za dobytok v údoliach Adlajov syn Šafát.",
			"30": "<sup>30</sup> Ťavy mal na starosti Izmaelita Obíl, oslice Meronoťan Jechdeja.",
			"31": "<sup>31</sup> Na drobné stáda dozeral Hagrijec Jazíz. Tí všetci boli správcami majetku kráľa Dávida.",
			"32": "<sup>32</sup> Dávidov strýko Jonatán, vzdelaný muž, bol poradcom a pisárom. Spolu s Jechíelom, Chachmoniho synom, vychovávali kráľových synov.",
			"33": "<sup>33</sup> Achitofel bol kráľovým poradcom, Arkíjec Chušaj kráľovým priateľom.",
			"34": "<sup>34</sup> Po Achitofelovi nasledovali: Benajov syn Jojada a Ebjatár. Veliteľom kráľovho vojska bol Joáb."
		},
		"28": {
			"1": "<sup>1</sup> Dávid zhromaždil do Jeruzalema všetkých izraelských hodnostárov: kmeňových náčelníkov a veliteľov oddielov, ktorí slúžili kráľovi, ďalej tisícnikov, stotníkov, správcov celého majetku i kráľovho stáda i jeho synov, dvoranov, hrdinov a všetkých udatných bojovníkov.",
			"2": "<sup>2</sup> Kráľ Dávid vstal a povedal: „Bratia moji a ľud môj, vypočujte ma! Sám som pomýšľal postaviť príbytok pre archu Hospodinovej zmluvy ako podnož nášho Boha a vykonal som prípravy na stavbu.",
			"3": "<sup>3</sup> Boh mi však povedal: ‚Ty nemôžeš postaviť dom môjmu menu, lebo si ako bojovník prelial krv.‘",
			"4": "<sup>4</sup> Hospodin, Boh Izraela, si z celej rodiny vyvolil mňa, aby som sa natrvalo stal kráľom nad Izraelom. Júdu si vyvolil za vojvodcu, v Júdovom dome zas moju rodinu a spomedzi mojich súrodencov sa mu zapáčilo mňa ustanoviť kráľom nad celým Izraelom.",
			"5": "<sup>5</sup> Zo všetkých mojich synov — Hospodin ma totiž obdaril mnohými synmi — si vyvolil môjho syna Šalamúna, aby zasadol na trón Hospodinovho kráľovstva nad Izraelom.",
			"6": "<sup>6</sup> Povedal mi: ‚Tvoj syn Šalamún mi postaví dom i nádvoria, lebo som si ho vyvolil za syna a budem mu otcom.",
			"7": "<sup>7</sup> Navždy upevním jeho kráľovstvo, ak bude svedomito uplatňovať moje príkazy a právne predpisy ako dnes.‘",
			"8": "<sup>8</sup> Teraz pred zrakom celého Izraela a zhromaždenia Hospodina a v prítomnosti nášho Boha vás napomínam: Pozorne skúmajte všetky prikázania Hospodina, svojho Boha, aby ste mohli vlastniť túto krásnu krajinu a odovzdali ju do trvalého dedičstva svojim potomkom.",
			"9": "<sup>9</sup> Ty však, Šalamún, syn môj, poznávaj otcovho Boha a slúž mu oddaným srdcom a ochotnou mysľou, lebo Hospodin skúma všetky srdcia a pozná všetko, čo myseľ vytvorí. Ak ho budeš hľadať, dá sa ti nájsť, ak ho však opustíš, zavrhne ťa navždy.",
			"10": "<sup>10</sup> Pováž teda, že si ťa zvolil Hospodin, aby si mu postavil murovanú svätyňu. Pusti sa smelo do práce!“",
			"11": "<sup>11</sup> Potom odovzdal Dávid svojmu synovi Šalamúnovi plán predsiene, chrámových miestností, jeho úschovní, horných a vnútorných miestností a miestnosť pre vrchnák archy,",
			"12": "<sup>12</sup> ďalej plán vlastných nápadov o nádvoriach Hospodinovho domu, o všetkých obvodových komorách, o pokladniciach Božieho domu a o pokladniciach pre zasvätené dary.",
			"13": "<sup>13</sup> Poskytol údaje o kňazských a levitských triedach, o všetkých bohoslužobných úkonoch a všetkých bohoslužobných predmetoch v Hospodinovom dome.",
			"14": "<sup>14</sup> Určil váhu zlata pre rôzne zlaté predmety používané pri jednotlivých službách, váhu striebra na rôzne strieborné predmety používané pri jednotlivých službách.",
			"15": "<sup>15</sup> Stanovil váhu zlata na zlaté svietniky a na ich zlaté lampy podľa váhy jednotlivých svietnikov a ich lámp, striebra na svietniky podľa váhy svietnika a jeho lámp, podľa účelu jednotlivých svietnikov.",
			"16": "<sup>16</sup> Navrhol primerané množstvo zlata na stoly pre predkladané chleby, na každý stôl zvlášť, a striebro na strieborné stoly,",
			"17": "<sup>17</sup> na vidlice, kropeničky a kanvice čisté zlato, na zlaté poháre primerané množstvo zlata podľa počtu pohárov, na strieborné poháre striebra podľa počtu pohárov.",
			"18": "<sup>18</sup> Na kadidlový oltár prikázal prečistené zlato podľa potrebnej váhy. Dal aj náčrt voza, zlatých cherubov, ktorí by rozprestretými krídlami zakrývali archu Hospodinovej zmluvy.",
			"19": "<sup>19</sup> „To všetko zapísala Hospodinova ruka, aby som mohol prezieravo pripraviť všetky stavebné práce.“",
			"20": "<sup>20</sup> Nato povedal Dávid svojmu synovi Šalamúnovi: „Pusti sa smelo a odvážne do práce! Neboj sa a nestrachuj, lebo Boh Hospodin, môj Boh, bude s tebou. Nenechá ťa a neopustí ťa, kým sa neskončí celé dielo na službu v Hospodinovom dome.",
			"21": "<sup>21</sup> Máš triedy kňazov a levitov na každú službu na Božom dome. Budú s tebou pri každej práci, aby sa celá služba diala ochotne a rozvážne. Máš i hodnostárov a všetok ľud pre tvoje rôzne potreby.“"
		},
		"29": {
			"1": "<sup>1</sup> Potom povedal kráľ Dávid celému zhromaždeniu: „Môj syn Šalamún, jediný, ktorého si Boh vyvolil, je neskúsený mladík. Samo dielo je však rozsiahle, lebo tá veľkolepá budova nie je určená pre človeka, ale pre Hospodina, Boha.",
			"2": "<sup>2</sup> Podľa svojich možností som zaobstaral pre dom svojho Boha zlato na zlaté, striebro na strieborné, bronz na bronzové, železo na železné, drevo na drevené predmety, ďalej ónyxové kamene, kamene na výzdobu, kamene malachitové a pestrofarebné, rôzne drahokamy a množstvo mramoru.",
			"3": "<sup>3</sup> Pretože mi záleží na dome svojho Boha, pridávam ešte z vlastného majetku zlato a striebro pre dom svojho Boha ako doplnok k tomu všetkému, čo som už zaobstaral pre svätyňu:",
			"4": "<sup>4</sup> tritisíc talentov zlata ofírskej akosti, sedemtisíc talentov pretaveného striebra na obloženie stien miestností,",
			"5": "<sup>5</sup> zlato na zlaté, striebro na strieborné predmety a pre rôznu prácu umelcov. Kto by chcel dnes ochotne niečo obetovať Hospodinovi?“",
			"6": "<sup>6</sup> Predáci rodín a izraelských kmeňov, tisícnici, stotníci i kráľovskí úradníci prispeli svojimi milodarmi.",
			"7": "<sup>7</sup> Na účely Božieho domu darovali päťtisíc talentov zlata, desaťtisíc dareikov, desaťtisíc talentov striebra, osemnásťtisíc talentov bronzu a stotisíc talentov železa.",
			"8": "<sup>8</sup> Kto mal nejaké drahokamy, daroval ich na poklad Hospodinovho domu do rúk Geršónovca Jechíela.",
			"9": "<sup>9</sup> Ľud sa radoval z ich milodarov, lebo z celého srdca ochotne obetovali Hospodinovi. Ba i kráľ Dávid sa nesmierne radoval.",
			"10": "<sup>10</sup> Dávid dobrorečil Hospodinovi pred celým zhromaždením takto: „Zvelebený buď Hospodin, Boh nášho praotca Izraela, od vekov až naveky!",
			"11": "<sup>11</sup> Tvoja je, Hospodin, veľkosť, moc, dôstojnosť, sláva i veleba. Patrí ti všetko na nebi i na zemi. Tvoje je, Hospodin, kráľovstvo; vypínaš sa nad všetkým ako hlava.",
			"12": "<sup>12</sup> Bohatstvo a česť sú od teba. Ty vládneš nad všetkým. V ruke máš silu a moc. Na tebe záleží, či urobíš niekoho veľkým a mocným.",
			"13": "<sup>13</sup> Teraz však, Bože náš, ďakujeme ti a chválime tvoje slávne meno.",
			"14": "<sup>14</sup> Ako to, že ja a môj ľud sme sa zmohli na toľké milodary? Keďže všetko od teba pochádza, dali sme ti vlastne z tvojho.",
			"15": "<sup>15</sup> Pokladáš nás len za cudzincov a prišelcov, akými boli všetci naši predkovia. Náš pozemský čas je ako tieň bez nádeje.",
			"16": "<sup>16</sup> Hospodin, Bože náš, všetka táto hojnosť, čo sme ti prichystali na stavbu domu pre tvoje sväté meno, pochádza od teba a tvoje je všetko.",
			"17": "<sup>17</sup> Viem však, Bože môj, že skúmaš srdce a obľubuješ si úprimnosť. Z úprimného srdca som priniesol všetky tieto milodary a teraz pozorujem, že i tu prítomný tvoj ľud rád obetuje.",
			"18": "<sup>18</sup> Hospodin, Bože našich praotcov Abraháma, Izáka a Izraela, zachovaj navždy takéto zmýšľanie svojho ľudu a jeho srdce pripútaj k sebe.",
			"19": "<sup>19</sup> Šalamúnovi, môjmu synovi, daj pevné odhodlanie zachovávať tvoje prikázania, svedectvá i ustanovenia a vykonať všetko pri stavbe chrámovej budovy, na ktorú som robil prípravy.“",
			"20": "<sup>20</sup> Potom Dávid vyzval celé zhromaždenie: „Dobrorečte Hospodinovi, svojmu Bohu!“ Celé to zhromaždenie dobrorečilo Hospodinovi, Bohu svojich otcov, a hlbokým úklonom vzdávalo poctu Hospodinovi i kráľovi.",
			"21": "<sup>21</sup> Prinášali Hospodinovi obetné dary a na druhý deň potom spaľované obety: tisíc býčkov, tisíc baranov, tisíc jahniat s príslušnými nápojovými obetami a obetných darov v hojnosti za celý Izrael.",
			"22": "<sup>22</sup> V ten deň jedli a pili pred Hospodinom za veľkej radosti a opätovne vyhlásili Dávidovho syna Šalamúna za kráľa. Pomazali ho za Hospodinovo knieža a Cadóka pomazali za kňaza.",
			"23": "<sup>23</sup> Tak zasadol Šalamún na Hospodinov trón ako kráľ po svojom otcovi Dávidovi. Úspešne si počínal a celý Izrael ho poslúchal.",
			"24": "<sup>24</sup> Všetci hodnostári, významné osobnosti a synovia kráľa Dávida sa kráľovi Šalamúnovi podriadili.",
			"25": "<sup>25</sup> Hospodin vysoko vyzdvihol Šalamúna v očiach celého Izraela. Dal mu taký kráľovský lesk, aký pred ním nemal žiaden kráľ nad Izraelom.",
			"26": "<sup>26</sup> Dávid, Izajov syn, vládol nad celým Izraelom.",
			"27": "<sup>27</sup> Kraľoval nad Izraelom spolu štyridsať rokov. V Hebrone vládol sedem rokov a v Jeruzaleme tridsaťtri rokov.",
			"28": "<sup>28</sup> Zomrel v úctyhodnej starobe, dosiahol vysoký vek, bohatstvo a slávu. Po ňom sa stal kráľom jeho syn Šalamún.",
			"29": "<sup>29</sup> Dávnejšie i novšie príbehy kráľa Dávida sú zapísané v Dejinách vidca Samuela, v Dejinách proroka Nátana a v Dejinách vidca Gáda.",
			"30": "<sup>30</sup> Hovoria o celej jeho vláde, o moci a o pomeroch, ktoré boli za neho v Izraeli a vo všetkých kráľovstvách krajín."
		}
	}
};
module.exports = book;